import { db } from "../../firebase";


export const handleDeactiveApp = (user,product,theApp) => {
    
    db.collection("users").doc(user.uid).get().then(snapshot =>{
      let theUser = snapshot.data();
      theUser.products = snapshot.data().products;
        

    //   theUser.products.forEach( product => {
    //     if(theProduct.name == product.name){
    //         product.apps.forEach(app => {
    //             if(app.name.name == theApp.name.name){
    //                 app.deviceId = " "
    //             }
    //                 console.log(app.deviceId)
    //         })
    //     }
    //   })
          console.log(theUser)
      snapshot.docs.forEach(p=>{
        let oneProduct = {};
        oneProduct.name = p.data().name;
        oneProduct.visibility = p.data().visibility;
        oneProduct.apps = p.data().apps;
        oneProduct.comments = p.data().comment;
        oneProduct.purchased = false;
        oneProduct.purchasedUrl = p.data().purchasedUrl;
        oneProduct.unpurchasedUrl = p.data().unpurchasedUrl;
        oneProduct.datePurchased = p.data().datePurchased;
        p.data().apps.forEach(app=>{
            oneProduct.apps.push({
                name:app,
                active:false,
                purchased: false,
                credentials : false,
                expireDate: "30/3/2021",
                timesAppLaunchedLastLogin : 3,
                timesAppLaunchedPurchased : 4,
                lastLaunchDate : "23-12-2020",
                timesAppLaunched : 7,
                deviceId : " "
            })
        })
        if(oneProduct.name == product.name){
            if(app == theApp.name){
                app.deviceId = " "
            }
        }
        theUser.products.push(oneProduct);
        console.log("the user is ", theUser.products)
      })

      return db.collection('users').set({...user,theUser}) 
      })
    .catch(err=>{
      console.log(err);
    })
  }
