import {db} from '../firebase';
// import {crypto} from 'crypto'
const crypto = require("crypto");

export const hadleAddCode = (code) => {
    const length = Number(code.lengthCode) 
    let cryptoCode = null;
    cryptoCode = crypto.randomBytes(length).toString("hex")
    db.collection("codes").add({
        code : code.perfixCode+cryptoCode,
        dateAdded : new Date().toLocaleString(),
        // productActivated : 
    })
    console.log(code.perfixCode+cryptoCode)
}
