import React,{useState, useEffect} from 'react'
import { useAuth } from "../contexts/AuthContext";
import { Link, useHistory } from "react-router-dom";
import { Navbar, Container, Row, Col, InputGroup, Button, FormControl} from "react-bootstrap";
import { db } from "../../firebase";


export default function UserPortal() {

  const [users, setUsers] = useState([]);
  const [deactiveAppUser, setDeactiveAppUser] = useState([]);

  useEffect(() => {
    db.collection("users").get()
    .then(snapshot=>{
      setUsers([...snapshot.docs]);
    })
  }, [])

    const { currentUser, logout } = useAuth();
    const history = useHistory();

    const [error, setError] = useState("");

    async function handleLogout() {
        setError("");
    
        try {
          await logout();
          history.push("/login");
        } catch {
          setError("Failed to log out");
        }
      }
      // handleLogout();
    
    const userCurrent = users.find(user => user.data().email == currentUser.email);

    useEffect(() => {
      let user = {};
      user.products = userCurrent.data().products;
      setDeactiveAppUser({...user});
    },[]);
    
    console.log(userCurrent.data());

    const deactiveAllApps = e => {
      e.preventDefault();
      userCurrent.data().products.map((p, i) => {
        p.apps.map((app,i) => {
          app.deviceId = ""
        })
        console.log(userCurrent.data())
      })
    }

    const handleDeactiveApp = (user,p,app,setUsers) => {
      let theUsers = []
    db.collection("users").doc(user.uid).docs(p.id).docs(app.id).update({
        deviceId: " "
        }).then(()=>{
            db.collection("users").get().then(snapshot=>{
                snapshot.docs.forEach(user=>{
                    theUsers.push(user);
                })
                setUsers([...theUsers]);
            })
        })
    .catch(err=>{
        console.log("Err is ", err)
    })
     
    }

    return (
      <div>
        <Container>
        <Navbar>
          <Navbar.Brand href="#home"></Navbar.Brand>
          <Navbar.Toggle />
          <Navbar.Collapse className="justify-content-end">
            <Navbar.Text>
            <button onClick={handleLogout}>logout</button>
            </Navbar.Text>
          </Navbar.Collapse>
        </Navbar>
        <div style={{paddingTop : 30, paddingBottom: 30}}>
          <Row>
            <Col>
              <div className="user_card">
                <strong className="mr-auto">Email: </strong>
                <p>{userCurrent.data().email}</p>
              </div>
            </Col>
            <Col>
              <div className="user_card">
                <strong className="mr-auto">Name: </strong>
                <p>{userCurrent.data().userName}</p>
              </div>
            </Col>
          </Row>
          <div className="user_card">
            <h4 className="mr-auto">Reset Passowrd: </h4>
            <ul>
              <li>instructions</li>
              <li>instructions</li>
              <li>instructions</li>
              <li>instructions</li>
            </ul>
          </div>
          <div className="user_card">
            <strong className="mr-auto">Code: </strong>
            <form onSubmit="">
            <InputGroup className="mb-3">
              <FormControl
                placeholder="Your Code"
                aria-label="Your Code"
                aria-describedby="Your Code"
              />
              <InputGroup.Append>
                <Button variant="secondary" type="submit">Submit</Button>  
              </InputGroup.Append>
            </InputGroup>
            </form>
          </div>
          <Row>
            {userCurrent.data().products.map((p, j) => (
              <Col md={6} key={j}>
                <div className="productUser" style={{borderColor: p.purchased ? "#218838" : "#AAA"}}>
                  <div style={{display: 'flex', justifyContent: "space-between"}}>
                    <h4>{p.name}</h4>
                    <Button style={{height:50, fontSize: 12}} variant="danger" onClick={deactiveAllApps}>Deactive all apps</Button>
                  </div>
                  <a href="">{p.purchased ? p.purchasedUrl : p.unpurchasedUrl}</a>
                  <div >
                    {p.apps.map((app,i) => {
                      return <div className="apps">
                                <div style={{display: "flex", justifyContent: "space-between", marginBottom: 20}}><h6>{app.name.name}</h6>
                                <Button className="ml-auto" style={{padding:5, fontSize: 12}} variant="danger" onClick={e => handleDeactiveApp(e,userCurrent.data(),p, app, setUsers)}>Deactive app</Button></div>
                                <p><strong>Expired Date : </strong>{app.expireDate}</p>
                                <p><strong>Last Launched Date : </strong>{app.lastLaunchDate}</p>
                                <p><strong>Times App Launched : </strong>{app.timesAppLaunched}</p>
                                <p><strong>Times App Launched LastLogin: </strong>{app.timesAppLaunchedLastLogin}</p>
                                <p><strong>Times App Launched Purchased : </strong>{app.timesAppLaunchedPurchased}</p>
                            </div>
                    })}
                  </div>
                </div>
              </Col>  
            ))}
          </Row>
        </div>
        </Container>
      </div>
    )
}
