import React, {useState, useRef, useEffect} from "react";
import {
  Button,
  Card,
  Table,
  Container,
  Row,
  Col,
  Form,
  OverlayTrigger,
  Tooltip,
  Modal,
  Tabs,
  Tab,
  InputGroup,
  FormControl
} from "react-bootstrap";


import {db} from '../firebase';
import {handleAddCode, 
        handleDeleteCode, 
        handleDeleteCodes, 
        handleAddCodes, 
        handleEditCode,
        handleEditCodes,
        handleDeleteInactiveCodes,
        handleReactiveCode
      } from './codeActions'

function AddCodes() {
    const [showCodePopup, setShowCodePopup] = useState(false);
    const [showEditCodePopup, setShowEditCodePopup] = useState(false);
    const [products, setProducts] = useState([]);
    const [allProducts, setAllProducts] = useState([]);
    const [addingNewCode, setAddingNewCode] = useState({});
    const [editingCode, setEditingCode] = useState({});
    const [viewInactiveCode, setViewInactiveCode] = useState({});
    const [codes, setCodes] = useState([]);
    const [searchedVal, setSearchedVal] = useState("");
    const [pageNum, setPageNum] = useState(5);
    const [selectedCodes, setSelectedCodes] = useState([]);
    const [selectedInactiveCodes, setSelectedInactiveCodes] = useState([]);
    const [inactiveCodes, setInactiveCodes] = useState([]);
    const [showInactiveCode, setshowInactiveCode] = useState(false);
    const allRef = useRef()

    useEffect(() => {
      db.collection("products").get().then(snapshot => {
        let allProducts =[];
        snapshot.docs.forEach(product => {
          let productApps = []
          product.data().apps.forEach((app) => {
            productApps.push({name : app.name, checked: false})
          })
          let featureApps = []
          product.data().features.forEach((feature) => {
            featureApps.push({name : feature.name, checked: false})
          })
          allProducts.push({
            productName : product.data().name,
            checked : false,
            productId : product.id,
            apps : productApps,
            features : featureApps
          })
          // console.log(allProducts)
        })
        setProducts([...allProducts])
    })
    }, [])

    useEffect(() => {
        db.collection("codes").get().then(snapshot => {
            let allCodes =[];
            snapshot.docs.forEach(code => {
                code.checked=false;
                allCodes.push(code)
            })
            setCodes([...allCodes])
        })
    },[])

     useEffect(() => {
        db.collection("inactiveCodes").get().then(snapshot => {
            let allCodes =[];
            snapshot.docs.forEach(code => {
                code.checked=false;
                allCodes.push(code)
            })
            setInactiveCodes([...allCodes])
        })
    },[])
 
     useEffect(() => {
        db.collection("products").get().then(snapshot => {
            let allProducts =[];
            snapshot.docs.forEach(product => {
              let productApps = []
              product.data().apps.forEach((app) => {
                productApps.push({name : app.name, checked: false})
              })
              let featureApps = []
              product.data().features.forEach((feature) => {
                featureApps.push({name : feature.name, checked: false})
              })
              allProducts.push({
                productName : product.data().name,
                checked : false,
                productId : product.id,
                apps : productApps,
                features : featureApps
              })
              // console.log(allProducts)
            })
            setProducts([...allProducts])
        })
    },[])

    // change search value
    const handleChangeSearchVal = e =>{
        setSearchedVal(e.target.value)
    }

    // handleChangePage
    const handleChangePage = e => {
    setPageNum(Number(e.target.value))
    }

    const toggleCodesSelection = (e)=>{
    let newSelectedCodes = [];
    let newCodes = codes;
    newCodes.forEach(code=>{
        code.checked = e.target.checked;
        if(e.target.checked){
          newSelectedCodes.push(code.id);
        }
    })
    setSelectedCodes([...newSelectedCodes])
    setCodes([...newCodes])
    }

    const toggleInactiveCodeSelection = e => {
      // console.log("wok")
      let newSelectedInactiveCodes = [];
      let newCodes = inactiveCodes;
      newCodes.forEach(code=>{
          code.checked = e.target.checked;
          if(e.target.checked){
            newSelectedInactiveCodes.push(code.id);
          }
      })
      setSelectedInactiveCodes([...newSelectedInactiveCodes])
      setInactiveCodes([...newCodes])
    }

    const handleSelectCode = (e,i,id)=>{
        let newSelectedCodes = selectedCodes;
        if(e.target.checked){
          newSelectedCodes.push(id);
        }else{
          newSelectedCodes = newSelectedCodes.filter((uid)=>{
            return uid !== id
          })
        }
        setSelectedCodes([...newSelectedCodes])
        let newCodes = codes;
        newCodes[i].checked = e.target.checked
        setCodes([...newCodes])
      }

    const showTheAddPop = ()=>{
        setShowCodePopup(true);
        let addingNewCode = {};
        addingNewCode.prefixCode  = "";
        addingNewCode.lengthCode  = 0;
        addingNewCode.purchasedLocation  = "";
        addingNewCode.apps  = [];
        addingNewCode.features = [];
        addingNewCode.products = products;
        addingNewCode.notes  = "";
        setAddingNewCode({...addingNewCode});
    }

    const handleOpenEditPopup = (id, i) => {
      setShowEditCodePopup(true)
      let EditingNewCode = {};
      EditingNewCode.id  = id;
      EditingNewCode.code  = codes[i].data().code;
      EditingNewCode.prefixCode  = codes[i].data().prefixCode;
      EditingNewCode.suffixCode  = codes[i].data().suffixCode;
      const activatedApps = parentFunc(codes[i].data().productActivated.apps,codes[i].data().productActivated.apps.length-1,"apps","appName",{})
      const activatedFeatures = parentFunc(codes[i].data().productActivated.features,codes[i].data().productActivated.features.length-1,"features","featureName",{})
      console.log("activatedApps",activatedApps,"activatedFeatures",activatedFeatures)
      EditingNewCode.apps  = [activatedApps,activatedFeatures];
      // EditingNewCode.apps  = codes[i].data().productActivated;
      EditingNewCode.notes  = codes[i].data().notes;
      setEditingCode({...EditingNewCode});
    }

    const closeAddPop = () => {
        setShowCodePopup(false)
        setShowEditCodePopup(false)
        setshowInactiveCode(false)
        setshowInactiveCode(false)
    }
    const handleAddingCodeprefix = (e) => {
        let newAddingCode = addingNewCode;
        newAddingCode.prefixCode = e.target.value;
        setAddingNewCode({...addingNewCode});
    }
    const handleAddingCodeLength = (e) => {
        let newAddingCode = addingNewCode;
        newAddingCode.lengthCode = e.target.value;
        setAddingNewCode({...addingNewCode});
    }
    const handleAddingpurchasedLocation = (e) => {
        let newAddingCode = addingNewCode;
        newAddingCode.purchasedLocation = e.target.value;
        setAddingNewCode({...addingNewCode});
    }
    const handleSelectedApps = (e, pIndex, appIndex) => {
        let newAddingCode = addingNewCode;
        if(e.target.checked){
          newAddingCode.products[pIndex].apps[appIndex].checked = true;
          newAddingCode.products[pIndex].checked = true;
        }
        setAddingNewCode({...addingNewCode});
    }
    const handleSelectedFeatures = (e, pIndex, appIndex) => {
      let newAddingCode = addingNewCode;
      if(e.target.checked){
        newAddingCode.products[pIndex].features[appIndex].checked = true;
        newAddingCode.products[pIndex].checked = true;
      }
      setAddingNewCode({...addingNewCode});
    }
    const handleNotesChange = e => {
      let newAddingCode = addingNewCode;
      newAddingCode.notes = e.target.value;
      setAddingNewCode({...addingNewCode});
    }
    const handleEditingNotesChange = e => {
      let newEditableCode = editingCode;
      newEditableCode.notes = e.target.value
      setEditingCode({...newEditableCode});
    }
    const handleEditingCodeprefix = (e)=>{
      let newEditableCode = editingCode;
      newEditableCode.prefixCode = e.target.value
      setEditingCode({...newEditableCode});
    }
    const handleEditSelectedApps = (e, pName, appName) => {
      let newEditableCode = editingCode;
        if(e.target.checked){
          newEditableCode.apps.push({productName:pName, appName: appName});
        }else{
          newEditableCode.apps = newEditableCode.apps.filter(p =>{
            return pName !== p.productName
          })
        }
      setEditingCode({...newEditableCode});
  }
    const handleAddingQuantity = e => {
      let newAddingCode = addingNewCode;
      newAddingCode.quantity = e.target.value;
      setAddingNewCode({...addingNewCode});
    }

    const handleOpenViewPopup = (code, i) => {
      // console.log(code.data())
      setshowInactiveCode(true)
      // const newInactiveCode = code.data().productActivated.apps.reduce(function(accumulator, currentValue) {
      //   console.log("accumulator",accumulator,"currentValue",currentValue);
      // }, 0);
       let viewCode = {};
        viewCode.code  = code.data().inactiveCode;
        viewCode.firstName  = code.data().firstName?code.data().firstName : null;
        viewCode.lastName  = code.data().lastName?code.data().lastName : null;
        viewCode.email  = code.data().activatedBy ;
        viewCode.apps  = code.data().productActivated;
        viewCode.notes  = code.data().notes;
        viewCode.id = code.id;
        viewCode.pefixCode = code.data().pefixCode
        viewCode.placeOfPurchase = code.data().placeOfPurchase;
        viewCode.dataAdded = code.data().dataAdded
        setViewInactiveCode({...viewCode});
    }

    const handleSelectedInactiveApps = (e, i, id) => {
      let newSelectedCodes = selectedCodes;
      if(e.target.checked){
        newSelectedCodes.push(id);
      }else{
        newSelectedCodes = newSelectedCodes.filter((uid)=>{
          return uid !== id
        })
      }
      setSelectedInactiveCodes([...newSelectedCodes])
      let newCodes = inactiveCodes;
      newCodes[i].checked = e.target.checked
      setInactiveCodes([...newCodes])
    }

    let productApps = null;
    productApps = products.map((p,i) => {
        return (
            <Col md="3" key={i}>
                <p style={{marginTop: 20}}>{p.productName}: </p>
                <div>{console.log(addingNewCode)}
                  {
                    p.apps.map((app,j) => {
                        return (<div style={{display: "flex"}} key={j}>
                        <Form.Check className="mb-1 pl-0" >
                        <Form.Check.Label>
                            <Form.Check.Input
                            onChange={(e)=> handleSelectedApps(e, i,j)}
                            // checked={app.checked}
                            defaultValue={app.checked}
                            type="checkbox"
                            ></Form.Check.Input>
                            <span className="form-check-sign"></span>
                            </Form.Check.Label>
                        </Form.Check>
                        <span style={{display: "inline-block", marginTop: 15}}>{app.name}</span>
                        </div>)
                    })
                  }
                </div>
                <div>
                  {
                    p.features.map((feature,k) => {
                        return (<div style={{display: "flex"}} key={k}>
                        <Form.Check className="mb-1 pl-0" >
                        <Form.Check.Label>
                            <Form.Check.Input
                            onChange={(e)=> handleSelectedFeatures(e, i, j)}
                            // checked={feature.checked}
                            defaultValue={feature.checked}
                            type="checkbox"
                            ></Form.Check.Input>
                            <span className="form-check-sign"></span>
                            </Form.Check.Label>
                        </Form.Check>
                        <span style={{display: "inline-block", marginTop: 15}}>{feature.name}</span>
                        </div>)
                    })
                  }
                </div>
            </Col>
        )
    });

    let renderActiveCodes = null;
    renderActiveCodes = codes.map((code,i) => {
      
      if(!codes.length){
            return;
        }
        if(i == pageNum){
        return ;
        }
        if(code.data().code.includes(searchedVal)){
        if(i < pageNum){
        return (
          <tr key={i}>
            <td>
          <Form.Check className="mb-1 pl-0">
          <Form.Check.Label>
              <Form.Check.Input
              onChange={(e)=> handleSelectCode(e,i,code.id)}
              checked={code.checked}
                  defaultValue=""
                  type="checkbox"
              ></Form.Check.Input>
              <span className="form-check-sign"></span>
              </Form.Check.Label>
          </Form.Check>
          </td>
            <td>
              {code.data().code}
            </td>
            <td>l,l</td>
            <td>{code.data().dateAdded}</td>
            <td>{code.data().purchasedLocation}</td>
            <td>
                <OverlayTrigger overlay={<Tooltip id="tooltip-422471719">Edit Task..</Tooltip>}>
                <Button className="btn-simple btn-link p-1"
                    type="button" variant="info"
                    onClick = {() => handleOpenEditPopup(code.id, i)}
                ><i className="fas fa-edit"></i> </Button>
                </OverlayTrigger>
                <OverlayTrigger
                overlay={<Tooltip id="tooltip-829164576">Remove..</Tooltip>}>
                <Button
                    className="btn-simple btn-link p-1"
                    type="button"
                    variant="danger"
                    onClick = {() => handleDeleteCode(code.id,codes,i,setCodes)}
                ><i className="fas fa-times"></i>
                </Button>
                </OverlayTrigger> 
            </td>
          </tr>
        )}}
    })

    let renderInactiveCodes = null;
      renderInactiveCodes = inactiveCodes.map((code,i) => {
        // {console.log(code.data())}
        if(!codes.length){
            return;
        }
        if(i == pageNum){
          return ;
        }
      //  if(code.data().inactiveCode.indexOf(searchedVal)){
        if(i < pageNum){
         return (
          <tr key={i}>
          <td>
          <Form.Check className="mb-1 pl-0">
          <Form.Check.Label>
              <Form.Check.Input
                onChange={(e)=> handleSelectedInactiveApps(e,i,code.id)}
                checked={code.checked}
                defaultValue=""
                type="checkbox"
              ></Form.Check.Input>
              <span className="form-check-sign"></span>
              </Form.Check.Label>
          </Form.Check>
          </td>
            <td>{code.data().inactiveCode}</td>
            <td>{code.data().activatedBy}</td>
            <td>{code.data().dataAdded}</td>
            <td>{code.data().placeOfPurchase}</td>
            <td>
                <OverlayTrigger overlay={<Tooltip id="tooltip-422471719">View Task..</Tooltip>}>
                <Button
                    className="btn-simple btn-link p-1"
                    type="button"
                    variant="info"
                    onClick = {() => handleOpenViewPopup(code, i)}
                >View</Button>
                </OverlayTrigger>
            </td>
          </tr>
        )}
      // }
    })

  return (
    <>
      <Container fluid>
        <Row>
          <Col md="12">
            <Card className="strpied-tabled-with-hover table-bordered">
              <Card.Header>
                <Card.Title as="h4">Codes</Card.Title>
              </Card.Header>
              <Card.Body>
                <div className="searchContainer">
                  <InputGroup className="mb-3 inputSearch" >
                    <InputGroup.Prepend>
                      <InputGroup.Text id="basic-addon1" autoComplete="on" ><i className="nc-icon nc-zoom-split"></i></InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl
                      placeholder="Search"
                      aria-label="Search"
                      aria-describedby="basic-addon1"
                      value={searchedVal}
                      onChange={handleChangeSearchVal}
                    />
                  </InputGroup>
                </div>
                <div className="pagination">
                  <span>Codes Per Page : </span>
                  <select  onChange={e => handleChangePage(e)}>
                    <option vlaue="5">5</option>
                    <option vlaue="10">10</option>
                    <option vlaue="15">15</option>
                    <option vlaue="20">20</option>
                    <option vlaue="25">25</option>
                  </select>
                </div>
                <Tabs defaultActiveKey="activateCode" id="uncontrolled-tab-example">
                  <Tab eventKey="activateCode" title="Active" style={{paddingTop:50}}>
                  <Table className="table-hover table-striped">
                    <thead>
                      <tr>
                        <th className="border-0">
                          <Form.Check  className="mb-1 pl-0" style={{display:"inline-block"}}>
                              <Form.Check.Label>
                                <Form.Check.Input
                                  onChange={toggleCodesSelection}
                                  ref={allRef}
                                  defaultValue="x"
                                  type="checkbox"
                                ></Form.Check.Input>
                                <span className="form-check-sign"></span>
                              </Form.Check.Label>
                            </Form.Check>
                            <span>all</span>
                          </th>
                        <th className="border-0">Code</th>
                        <th className="border-0">Product Activated</th>
                        <th className="border-0">Date Added</th>
                        <th className="border-0">Place Of Purchase</th>
                        <th className="border-0">
                          <button onClick={showTheAddPop}>Add Code</button><br/>
                          <button onClick={() => handleDeleteCodes(selectedCodes, setCodes)}>Delete Selected Codes</button><br/>
                          <button onClick={() => console.log("Deleted Selected")}>Export Selected Code to CSV</button><br/>
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                    {renderActiveCodes}
                    </tbody>
                  </Table>
                  </Tab>
                  <Tab eventKey="inActivateCode" title="Inactive" style={{paddingTop:50}}>
                  <Table className="table-hover table-striped">
                    <thead>
                      <tr>
                        <th className="border-0">
                          <Form.Check  className="mb-1 pl-0" style={{display:"inline-block"}}>
                              <Form.Check.Label>
                                <Form.Check.Input
                                  onChange={toggleInactiveCodeSelection}
                                  ref={allRef}
                                  defaultValue="x"
                                  type="checkbox"
                                ></Form.Check.Input>
                                <span className="form-check-sign"></span>
                              </Form.Check.Label>
                            </Form.Check>
                            <span>all</span>
                          </th>
                        <th className="border-0">Code</th>
                        <th className="border-0">Activated By</th>
                        <th className="border-0">Date Activated</th>
                        <th className="border-0">Place Of Purchase</th>
                        <th className="border-0">
                          {/* <button onClick={showTheAddPop}>Add Code</button> */}
                          <button onClick={() => handleDeleteInactiveCodes(selectedInactiveCodes, setInactiveCodes)}>Delete Selected Codes</button>
                          <button onClick={() => console.log("Deleted Selected")}>Export Selected Code to CSV</button>
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                    {renderInactiveCodes}
                    </tbody>
                  </Table>
                  </Tab>
                </Tabs>
              </Card.Body>
            </Card>
          </Col>
        </Row>
          <div className={"thePopUp " + (showCodePopup ? "active" : "")} >
            <div className="thePopUpBody" style={{maxWidth: "770px "}}>
                  <div className="font-icon-detail closePopUp" onClick={closeAddPop}>
                  <i className="nc-icon nc-simple-remove"></i>
                  </div>
                  <h2 style={{marginBottom:50}}>Add Code</h2>
              <Tabs defaultActiveKey="SingleCode" id="uncontrolled-tab-example">
                <Tab eventKey="SingleCode" title="Single Code" style={{paddingTop: 40}}>
                  <Form.Group>
                  <Row>
                      <Col md="4"><Row><Col xs="auto"><label>Code prefix: </label></Col>
                  <Col>
                  <Form.Control
                          defaultValue={addingNewCode.prefixCode }
                          type="text"
                          onChange={handleAddingCodeprefix}
                      ></Form.Control></Col></Row></Col>
                      <Col md="4">
                      <Button
                          className="btn-fill pull-right"
                          type="submit"
                          variant="secondary"
                          onClick={()=> console.log("Add Code")}
                      >
                          Generate Random Code
                      </Button>
                      </Col>
                      <Col md="4">
                      <Row><Col xs="auto"><label>Code Length: </label></Col>
                  <Col>
                  <Form.Control
                          defaultValue={addingNewCode.lengthCode}
                          type="number"
                          onChange={handleAddingCodeLength}
                      ></Form.Control></Col></Row>
                      </Col>
                  </Row>
                  </Form.Group>
                  <Form.Group>
              <Row>
                  <Col xs="auto"><label>Purchased Location: </label></Col>
                  <Col xs="auto"><Form.Control
                          defaultValue={addingNewCode.purchasedLocation}
                          placeholder="Add Purchased Location"
                          type="text"
                          onChange={handleAddingpurchasedLocation}
                      ></Form.Control></Col>
                  </Row>
                  </Form.Group>
                  <hr />
                  <h5>Products To Activate</h5>
                  <Row>{productApps }</Row>
                  <hr style={{marginTop: 30}}/>
                  <Row>
                  <Col xs="12"><Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label>Notes: </Form.Label>
                      <Form.Control as="textarea" rows={3} defaultValue={addingNewCode.notes} onChange={handleNotesChange}/>
                  </Form.Group>
                  </Col>
                  </Row>
                  <Row>
                    <Col>
                    <Button
                        className="btn-fill pull-right"
                        type="submit"
                        variant="info"
                        onClick={() => handleAddCode(addingNewCode, setCodes,closeAddPop)}
                      >
                        Add Code
                      </Button>
                    </Col>
                </Row>
                </Tab>
              <Tab eventKey="BulkCode" title="Bulk Code" style={{paddingTop: 40}}>
              <Form.Group>
                  <Row>
                      <Col md="6"><Row><Col xs="auto"><label>Code prefix: </label></Col>
                  <Col>
                  <Form.Control
                          defaultValue={addingNewCode.prefixCode }
                          type="text"
                          onChange={handleAddingCodeprefix}
                      ></Form.Control></Col></Row></Col>
                      <Col md="6">
                      <Row><Col xs="auto"><label>Code Length: </label></Col>
                  <Col>
                  <Form.Control
                          defaultValue={addingNewCode.lengthCode}
                          type="number"
                          onChange={handleAddingCodeLength}
                      ></Form.Control></Col></Row>
                      </Col>
                  </Row>
                  </Form.Group>
                  <Form.Group>
                  <Row>
                      <Col md="6"><Row><Col xs="auto"><label>Code Quantity: </label></Col>
                  <Col>
                  <Form.Control
                      defaultValue={addingNewCode.quantity }
                      type="number"
                      onChange={handleAddingQuantity}
                    ></Form.Control></Col></Row></Col>
                    <Col md="6">
                    <Row><Col xs="auto"><label>Purchased Location: </label></Col>
                  <Col>
                  <Form.Control
                          defaultValue={addingNewCode.purchasedLocation}
                          type="text"
                          onChange={handleAddingpurchasedLocation}
                      ></Form.Control></Col></Row>
                      </Col>
                  </Row>
                  </Form.Group>
                  <hr />
                  <h5>Products To Activate</h5>
                  <Row>{productApps }</Row>
                  <hr style={{marginTop: 30}}/>
                  <Row>
                  <Col xs="12"><Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label>Notes: </Form.Label>
                      <Form.Control as="textarea" rows={3} defaultValue={addingNewCode.notes} onChange={handleNotesChange}/>
                  </Form.Group>
                  </Col>
                  </Row>
                  <Row>
                    <Col>
                    <Button
                        className="btn-fill pull-right"
                        type="submit"
                        variant="info"
                        onClick={() => handleAddCodes(addingNewCode, setCodes,closeAddPop)}
                      >
                        Add Codes
                      </Button>
                    </Col>
                </Row>
              </Tab>
              </Tabs>
            </div>
          </div>

          <div className={"thePopUp " + (showEditCodePopup ? "active" : "")} >
            <div className="thePopUpBody" style={{maxWidth: "770px "}}>
                  <div className="font-icon-detail closePopUp" onClick={closeAddPop}>
                  <i className="nc-icon nc-simple-remove"></i>
                  </div>
                  <h2 style={{marginBottom:50}}>Edit Code</h2>
                  {
                    Array.isArray(editingCode.code) ? null : <Form.Group><Row>
                    <Col xs="auto"><label>Code prefix: </label></Col>
                    <Col xs="auto">
                    <Form.Control defaultValue={editingCode.prefixCode } type="text" onChange={handleEditingCodeprefix}
                        ></Form.Control></Col>
                    </Row></Form.Group>
                  }
                  <hr />
                  <h5>Products To Activate</h5>
                  <Row>
                    {
                      products.map((p,i) => {
                        return (
                        <Col md="3" key={i}>
                          <p style={{marginTop: 20}}>{p.name}: </p>
                          {
                           editingCode.apps ? Object.keys(editingCode.apps[0]).map((activeApp) => {
                            if(activeApp == p.name){
                            return p.apps.map((app) => {
                            return editingCode.apps[0][activeApp] ? Object.keys(editingCode.apps[0][activeApp]).map((appArr) => {
                              return Object.keys(editingCode.apps[0][activeApp][appArr]).map(appName => {
                              
                              if(app.app.name == editingCode.apps[0][activeApp][appArr][appName]){
                                return (<div style={{display: "flex"}}> 
                                  <Form.Check className="mb-1 pl-0" >
                                  <Form.Check.Label>
                                    <Form.Check.Input
                                    onChange={(e)=> handleEditSelectedApps(e, p.name, editingCode.apps[0][activeApp][appArr][appName])}
                                    checked={true}
                                    defaultValue={true}
                                    type="checkbox"
                                    ></Form.Check.Input>
                                    <span className="form-check-sign"></span>
                                    </Form.Check.Label>
                                  </Form.Check>
                                  <span style={{display: "inline-block", marginTop: 15}}>{app.app.name}</span>
                                  </div> )
                              }else if(app.app.name !== editingCode.apps[0][activeApp][appArr][appName]){
                                return (<div style={{display: "flex"}}> 
                                  <Form.Check className="mb-1 pl-0" >
                                  <Form.Check.Label>
                                    <Form.Check.Input
                                    onChange={(e)=> handleEditSelectedApps(e, p.name, editingCode.apps[0][activeApp][appArr][appName])}
                                    checked={false}
                                    defaultValue={false}
                                    type="checkbox"
                                    ></Form.Check.Input>
                                    <span className="form-check-sign"></span>
                                    </Form.Check.Label>
                                  </Form.Check>
                                  <span style={{display: "inline-block", marginTop: 15}}>{app.app.name}</span>
                                  </div> )
                              }
                              
                            })
                            }) : null
                          })
                            }else{
                              return p.apps.map((app) => {
                                return (<div style={{display: "flex"}}> 
                                    <Form.Check className="mb-1 pl-0" >
                                    <Form.Check.Label>
                                        <Form.Check.Input
                                        onChange={(e)=> handleEditSelectedApps(e, p.name, editingCode.apps[0][activeApp][appArr][appName])}
                                        checked={false}
                                        defaultValue={false}
                                        type="checkbox"
                                        ></Form.Check.Input>
                                        <span className="form-check-sign"></span>
                                        </Form.Check.Label>
                                    </Form.Check>
                                    <span style={{display: "inline-block", marginTop: 15}}>{app.app.name}</span>
                                    </div> )
                              })
                            }
                          }) : null
                          }
                          
                        </Col>
                      )
                      })
                    }
                  </Row>
                  <hr style={{marginTop: 30}}/>
                  <Row>
                  <Col xs="12"><Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label>Notes: </Form.Label>
                      <Form.Control as="textarea" rows={3} defaultValue={editingCode.notes} onChange={handleEditingNotesChange}/>
                  </Form.Group>
                  </Col>
                  </Row>
              <Row>
              <Col>
              {
                Array.isArray(editingCode.code)?<Button className="btn-fill pull-right"
                    type="submit" variant="info" onClick={() => handleEditCodes(editingCode, setCodes,closeAddPop)}
                  >Save Changes</Button>:<Button className="btn-fill pull-right" type="submit" variant="info" onClick={() => handleEditCode(editingCode, setCodes,closeAddPop)}
                    >Save Changes</Button>
              }
              </Col>
          </Row>
            </div>
          </div>

          
          <div className={"thePopUp " + (showInactiveCode ? "active" : "")} >
            <div className="thePopUpBody" style={{maxWidth: "770px "}}>
                  <div className="font-icon-detail closePopUp" onClick={closeAddPop}> 
                  <i className="nc-icon nc-simple-remove"></i>
                  </div>
                  <h2 style={{marginBottom:50}}>View Inactive Code</h2>
                  <h5><strong>Code: </strong>{viewInactiveCode.code}</h5>
                  <h5><strong>First Name: </strong>{viewInactiveCode.firstName}</h5>
                  <h5><strong>Last Name: </strong>{viewInactiveCode.lastName}</h5>
                  <h5><strong>Email: </strong>{viewInactiveCode.email}</h5>
                  <h5>Products To Activate</h5>
                  <Row>
                    {
                      allProducts.map((product,i) => {
                        return (<Col md="3" key={i}>
                          {viewInactiveCode.apps? viewInactiveCode.apps.apps.map((p,l) => {
                            if(product.data().name == p.productName){
                              return(<div key={l}>
                              {
                                product.data().apps.map((app,j)=>{
                                  return(<div>
                                    <p>{product.data().name}</p>
                                    <div style={{display: "flex"}} key={j}>
                                  <Form.Check className="mb-1 pl-0" >
                                  <Form.Check.Label>
                                    <Form.Check.Input
                                    // onChange={(e)=> handleEditSelectedApps(e, p.name, app.app.name)}
                                    checked={app.name == p.appName ? true : false}
                                    defaultValue={true}
                                    type="checkbox"
                                    ></Form.Check.Input>
                                    <span className="form-check-sign"></span>
                                  </Form.Check.Label>
                                  </Form.Check>
                                  <span style={{display:"inline-block", marginTop: 15}}>{app.name}</span>
                                  </div>
                                </div>)
                                })
                              } 
                             </div>) 
                             }
                          }):null}
                        </Col>)
                      })
                    }
                  </Row>
                  <hr style={{marginTop: 30}}/>
                  <Row>
                  <Col xs="12"><Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label>Notes: </Form.Label>
                      <Form.Control as="textarea" rows={3} defaultValue={viewInactiveCode.notes}/>
                  </Form.Group>
                  </Col>
                  </Row>
              <Row>
              <Col>
               <Button className="btn-fill pull-right" type="submit" variant="info" 
               onClick={() => handleReactiveCode(viewInactiveCode, setInactiveCodes,closeAddPop)}>Reactive</Button>
               <Button className="btn-fill pull-right" type="submit" variant="info" 
               onClick={closeAddPop}>Cancel</Button>
              </Col>
          </Row>
            </div>
          </div>
          
      </Container>
    </>
  );
}

export default AddCodes;
