import React,{useState, useEffect} from 'react'
import { useAuth } from "../contexts/AuthContext";
import { Link, useHistory } from "react-router-dom";
import { Navbar, Container, Row, Col, InputGroup, Button} from "react-bootstrap";
import { db } from "../../firebase";


export default function UserPortal() {

  const [users, setUsers] = useState([]);

  useEffect(() => {
    db.collection("users").get()
    .then(snapshot=>{
      setUsers([...snapshot.docs]);
    })
  }, [])

    const { currentUser, logout } = useAuth();
    const history = useHistory();

    const [error, setError] = useState("");

    async function handleLogout() {
        setError("");
    
        try {
          await logout();
          history.push("/login");
        } catch {
          setError("Failed to log out");
        }
      }
      // handleLogout();
    
    const userCurrent = users.filter(user => user.data().email == currentUser.email)
    
    console.log(userCurrent[0].data())
    return (
        <div>
          <Container>
          <Navbar>
            <Navbar.Brand href="#home"></Navbar.Brand>
            <Navbar.Toggle />
            <Navbar.Collapse className="justify-content-end">
              <Navbar.Text>
              <button onClick={handleLogout}>logout</button>
              </Navbar.Text>
            </Navbar.Collapse>
          </Navbar>
          <div>
            <Row>
              <Col>
                <div className="user_card">
                  <strong className="mr-auto">Email: </strong>
                  <p>{userCurrent[0].data().email}</p>
                </div>
              </Col>
              <Col>
                <div className="user_card">
                  <strong className="mr-auto">Name: </strong>
                  <p>{userCurrent[0].data().userName}</p>
                </div>
              </Col>
            </Row>
            <div className="user_card">
              <h4 className="mr-auto">Reset Passowrd: </h4>
              <ul>
                <li>instructions</li>
                <li>instructions</li>
                <li>instructions</li>
                <li>instructions</li>
              </ul>
            </div>
            <Row>
              {userCurrent[0].data().products.map((p, i) => (
                <Col md={6} key={i}>
                  <div className="productUser" style={{borderColor: p.purchased ? "#218838" : "#AAA"}}>
                    <a href="">{p.purchased ? p.purchasedUrl : p.unpurchasedUrl}</a>
                  </div>
                </Col>
              ))}
            </Row>
            <div className="user_card">
              <strong className="mr-auto">Code: </strong>
              <form onSubmit="">
              <InputGroup className="mb-3">
                <FormControl
                  placeholder="Your Code"
                  aria-label="Your Code"
                  aria-describedby="Your Code"
                />
                <InputGroup.Append>
                  <Button variant="secondary" type="submit">Submit</Button>  
                </InputGroup.Append>
              </InputGroup>
              </form>
            </div>
          </div>
          </Container>
        </div>
    )
}
