import React, {useRef,useEffect,useState } from "react";
import ChartistGraph from "react-chartist";
import app, { db } from "../firebase";
import {handleDeleteUser,handleDeleteUsers, handleUpdateUser} from './UsersAction'

// react-bootstrap components
import {
  Badge,
  Button,
  Card,
  Navbar,
  Nav,
  Table,
  Container,
  Row,
  Col,
  Form,
  OverlayTrigger,
  Tooltip,
  Modal
} from "react-bootstrap";
import Toast from 'react-bootstrap/Toast'


function Dashboard() {
  const allRef = useRef();
  const [users,setUsers] = useState([]) 
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [editableUser,setEditableUser] = useState({
    email:"",
    products:[]
  });
  const [modalShow, setModalShow] = React.useState(false);
  const [CommentVal, setCommentVal] = React.useState('');

  const closeEditPop = ()=>{
    setModalShow(false);
}

  useEffect(() => {
    db.collection("users").get()
    .then(snapshot=>{
      let users = [];
      snapshot.docs.forEach(user=>{
        user.checked = false;
        user.purchased = false;
        users.push(user);
      })
      setUsers([...users]);

      let newEditableUser = {};
      newEditableUser.uid      = users[0].id;
      newEditableUser.email    = users[0].data().email;
      newEditableUser.products = users[0].data().products;
      setEditableUser({...newEditableUser});
    })
  },[]);

  const handleSelectUser = (e,i,id)=>{
    let newSelectedUsers = selectedUsers;
    if(e.target.checked){
      newSelectedUsers.push(id);
    }else{
      newSelectedUsers = newSelectedUsers.filter((uid)=>{
        return uid !== id
      })
    }
    setSelectedUsers([...newSelectedUsers])
    let newUsers = users;
    newUsers[i].checked = e.target.checked
    setUsers([...newUsers])
  }

  const toggleUsersSelection = (e)=>{
    let newSelectedUsers = [];
    let newUsers = users;
    newUsers.forEach(user=>{
      user.checked = e.target.checked;
      if(e.target.checked){
        newSelectedUsers.push(user.id);
      }
    })
    setSelectedUsers([...newSelectedUsers])
    setUsers([...newUsers])
  }

  const handleEditUser = (uid,i)=>{
    let newEditableUser = {};
    newEditableUser.uid      = users[i].id;
    newEditableUser.email    = users[i].data().email;
    newEditableUser.products = users[i].data().products;
    newEditableUser.products.comments = CommentVal;
    setEditableUser({...newEditableUser});
    setModalShow(true)

  }

  const hanldeChangeEmailForEditableUser = (e)=>{
    let newEditableUser = editableUser;
    newEditableUser.email = e.target.value;

    setEditableUser({...newEditableUser})
  }
const toggleCheckApp = (e,pIndex,appIndex)=>{
  console.log(e.target.checked)
  let newEditableUser = editableUser;
  editableUser.products[pIndex].apps[appIndex].active = e.target.checked;
  setEditableUser({...editableUser})
}

// toggole in purchased button
const toggleCheckPurchasedApp = (e,pIndex,appIndex)=>{
  let newEditableUser = editableUser;
  editableUser.products[pIndex].apps[appIndex].purchased = e.target.checked;
  setEditableUser({...editableUser})
}

// toggle Credentials App
const toggleCredentialsApp = (e,pIndex,appIndex)=>{
  let newEditableUser = editableUser;
  editableUser.products[pIndex].apps[appIndex].credentials = e.target.checked;
  setEditableUser({...editableUser})
}

// if(!editableUser.hasOwnProperty("products")){
        //   return;
        // }
        // if(!editableUser.products.length){
        //   return
        // }
// add Comments
  const handleChange = (e, pIndex) => {
    e.preventDefault();
    e.stopPropagation();
    let newEditableUser = editableUser;
    editableUser.products[pIndex].comments = e.target.value;
    setEditableUser({...editableUser})
  }

  // change expire date
  const handleChangeExpireDate = (e, pIndex, appIndex) => {
    let newEditableUser = editableUser;
    editableUser.products[pIndex].apps[appIndex].expireDate = e.target.value;
    setEditableUser({...editableUser})
  }


  // check app box
  const toggleShow = (e, pIndex, appIndex) => {
    let newEditableUser = editableUser;
    editableUser.products[pIndex].apps[appIndex].visblility = e.target.checked;
    setEditableUser({...editableUser})
  }

  let renderProductsForUser = null;
      renderProductsForUser = editableUser.products.map((p,j)=>{

        return <Row key={j} className="pCategory">
          <Col sm="12">
          <span style={{width:"100%"}}>{p.name}</span>
          </Col>
          <br/>
          <Col sm="12">
            {
              p.url ? <a href="+{p.url}+">Product Page</a> : ""
            }
          </Col>
          <Col sm="12">
          {/* {
            const PurchasedApp = p.apps.filter(app => app.active === true);
            PurchasedApp.map(app => (
              <span>{app.name}</span>
            ))
          } */}
          </Col>
          {p.apps.map((app,i)=>{
            return <Col md="6"><div className="oneProduct" key={i}>
                <h6> {app.name}</h6>
                <div>
                <span>Active : </span>
                <label className="switch">
                  <input type="checkbox" checked={app.active} onChange={(e)=>{toggleCheckApp(e,j,i)}} />
                  <span className="slider round"></span>
                </label>
                </div>
                <div>
                <span>purchased : </span>
                <label className="switch">
                  <input type="checkbox" checked={app.purchased} onChange={(e)=>{toggleCheckPurchasedApp(e,j,i)}} />
                  <span className="slider round"></span>
                </label>
              </div>
              {
                app.purchased ? (<div><label for="expireDate">ExpireDate:</label><input type="date" id="expireDate" name="expireDate" onChange={e => handleChangeExpireDate(e,j,i)}/></div>):""
              }
              <div>
              <span>Visblility : </span>
              <label className="switch">
                <input type="checkbox" checked={app.visblility} onChange={(e)=>{toggleShow(e,j,i)}} />
                <span className="slider round"></span>
              </label>
              </div>
              <div>
              <span>Credentials : </span>
              <label className="switch">
                <input type="checkbox" checked={app.credentials} onChange={(e)=>{toggleCredentialsApp(e,j,i)}} />
                <span className="slider round"></span>
              </label>
              </div>
              <div>
                {
                  app.purchased ? <a href="+{app.name}+">App Link</a> : ''
                }
                {
                  !app.purchased ? <a href="+{app.name}+">App Link</a> : ''
                }
              </div>
              <div className="appDtail">
                <p><span># of times app launched since last login</span><span></span></p>
                <p><span># of times app launched since purchased</span><span></span></p>
                <p><span>Date of last launch</span><span></span></p>
                <p><span># of times app launched</span><span></span></p>
              </div>

            </div></Col>
            })}
            <Col md="4"><Form.Group controlId="exampleForm.ControlTextarea1">
              <Form.Label>Comments</Form.Label>
              <Form.Control as="textarea" rows={3} onChange={(e) => handleChange(e,j)}/>
            </Form.Group></Col>
          </Row>

      })

  // editing user modal
  const MyVerticallyCenteredModal = (props) => {
    return (
      <div className={"thePopUp editUserPopUp" + (modalShow ? "active" : "")} >
          <div className="thePopUpBody">

            <div className="font-icon-detail closePopUp" onClick={closeEditPop}>
              <i className="nc-icon nc-simple-remove"></i>
            </div>

        <Modal.Header >
          <Modal.Title >
            <h4 className="card-title">Edit User</h4>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
              <div className="userDetails">
                <h6>User Details</h6>
                    <Form.Group>
                        <label>Email</label>
                        <Form.Control
                            defaultValue={editableUser.email}
                            placeholder="Add Product Apps Separated by Comma"
                            type="text"
                            onChange={hanldeChangeEmailForEditableUser}
                        ></Form.Control>
                    </Form.Group>
                    <div>
                      <h4>products purchased <span> </span></h4>
                      <h4>Date purchased <span> </span></h4>
                    </div>
                  <h4>Manage Users Products</h4>
                  {renderProductsForUser}
              </div>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Close</Button>
          <Button onClick={()=>{handleUpdateUser(editableUser,setUsers,closeEditPop)}}>Save</Button>
        </Modal.Footer>
          </div>
      </div>
    );
  }


// Render all users
  let renderUsers = null;
      renderUsers = users.map((user,i)=>{
        if(!users.length && users.length > 25){
          return;
        }
        // if(i==pageNum){
        //   return;
        // }
        //     if(user.data().email.indexOf(searchedVal)){
        //       return <tr
        //     }
        return <tr key={user.id}>
        <td><Form.Check className="mb-1 pl-0">
              <Form.Check.Label>
                <Form.Check.Input
                  onChange={(e)=>{handleSelectUser(e,i,user.id)}}
                  checked={user.checked}
                  defaultValue=""
                  type="checkbox"
                ></Form.Check.Input>
                <span className="form-check-sign"></span>
              </Form.Check.Label>
            </Form.Check>
          </td>
        <td>{user.data().email}</td>
        <td>{user.data().creationDate}</td>
        <td>{user.data().lastLoginDate}</td>
        <td>

        <OverlayTrigger
              overlay={
                <Tooltip id="tooltip-422471719">
                  Edit Task..
                </Tooltip>
              }
            >
              <Button
                onClick={()=>{handleEditUser(user.id,i)}}
                className="btn-simple btn-link p-1"
                type="button"
                variant="info"
              >
                <i className="fas fa-edit"></i>
              </Button>
            </OverlayTrigger>
            <OverlayTrigger
              overlay={
                <Tooltip id="tooltip-829164576">Remove..</Tooltip>
              }
            >
              <Button
                onClick={()=>handleDeleteUser(user.id,users,i,setUsers)}
                className="btn-simple btn-link p-1"
                type="button"
                variant="danger"
              >
                <i className="fas fa-times"></i>
              </Button>
            </OverlayTrigger>
        </td>
      </tr>
      })

  return (
    <>
      <Container fluid>
        <div className="text-right">
        <Button className="mr-2" variant="secondary">Users</Button> 
        <Button className="mr-2" variant="secondary">Codes</Button> 
        <Button className="mr-2" variant="secondary">Used Codes</Button> 
        <Button variant="secondary">Settings</Button>
        </div>
      <Row> 
          <Col>
            <Card className="card-tasks">
              <Card.Header>
                <Card.Title as="h4">Users</Card.Title>
                <p className="card-category">List of userse where you can manage them</p>
              </Card.Header>
              <Card.Body>

              <Table className="table-hover table-striped">
                  <thead>
                    <tr>
                      <th className="border-0">

                         <Form.Check  className="mb-1 pl-0" style={{display:"inline-block"}}>
                            <Form.Check.Label>
                              <Form.Check.Input
                                onChange={toggleUsersSelection}
                                ref={allRef}
                                defaultValue="x"
                                type="checkbox"
                              ></Form.Check.Input>
                              <span className="form-check-sign"></span>
                            </Form.Check.Label>
                          </Form.Check>
                          <span>all</span>
                        </th>
                      <th className="border-0">Email</th>
                      <th className="border-0">Creation Date</th>
                      <th className="border-0">Last Login Date</th>
                      <th className="border-0">
                        <button onClick={()=>{handleDeleteUsers(selectedUsers)}}>delete multiple user</button>
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                  {renderUsers}
                  </tbody>
                </Table>
              </Card.Body>
            </Card>           
          </Col>      
      </Row>
          <MyVerticallyCenteredModal
            show={modalShow}
            onHide={() => setModalShow(false)}
          />
      </Container>
    </>
  );
}

export default Dashboard;
