import { functions, db } from "../firebase";
const deleteUser  = functions.httpsCallable("deleteUser");
const deleteUsers = functions.httpsCallable("deleteUsers");
const updateUser  = functions.httpsCallable("updateUser");

export const handleUpdateUser = (user,setUsers,callback)=>{
    let theUsers = []
    return updateUser(user)
    .then(()=>{
        db.collection("users").doc(user.uid).update({
            email:user.email,
            products:user.products
        }).then(()=>{
            db.collection("users").get().then(snapshot=>{
                snapshot.docs.forEach(user=>{
                    user.checked = false;
                    user.purchased = false;
                    theUsers.push(user);
                })
                setUsers([...theUsers]);
                callback()
            })
        })

    })
    .catch(err=>{
        console.log("Err is ", err)
    })
}




export const handleDeleteUser = (uid,users,i,setUsers)=>{
    users.splice(i, 1);
    setUsers([...users]);

    return deleteUser({uid:uid})
    .then(()=>{
        db.collection("users").doc(uid).delete();

    })
    .catch(err=>{
        console.log("Err is ", err)
    })
}
export const handleDeleteUsers = (ids) =>{
    return deleteUsers(ids)
    .then(res=>{
        console.log("res after deleting users",res);
        ids.forEach(uid => {
            db.collection("users").doc(uid).delete();
        });
    }).catch(err=>{
        console.log("ERR",err);
    })
}




const deleteDoc = (n)=>{
    if(n == 0) return;

    console.log(n);
}

const returnFilteredUsers = (users,userId)=>{
    let newUsers = users.filter(user=>{
        return user.id !== userId;
    })
    return newUsers; 
}
