import React, {useState, useRef, useEffect} from "react";
import {
    Button,
    Card,
    Table,
    Container,
    Row,
    Col,
    Form,
    OverlayTrigger,
    Tooltip,
    Modal,
    Tabs,
    Tab,
    InputGroup,
    FormControl
} from "react-bootstrap";


import {db} from '../firebase';
import {handleAddCode, handleDeleteCode} from './codeActions'

function AddCodes() {
    const [showCodePopup, setShowCodePopup] = useState(false);
    const [products, setProducts] = useState([]);
    const [addingNewCode, setAddingNewCode] = useState({});
    const [codes, setCodes] = useState([]);
    const [searchedVal, setSearchedVal] = useState("");
    const [pageNum, setPageNum] = useState(5);
    const [selectedCodes, setSelectedCodes] = useState([]);

    useEffect(() => {
        db.collection("products").get().then(snapshot => {
            let allProducts = [];
            snapshot.docs.forEach(p => {
                let oneProduct = {};
                oneProduct.name = p.data().name;
                oneProduct.apps = [];
                oneProduct.purchasedUrl = p.data().purchasedUrl;
                oneProduct.unpurchasedUrl = p.data().unpurchasedUrl;
                oneProduct.productIcon = p.data().productIcon;
                oneProduct.description = p.data().description;
                oneProduct.id = p.id;
                p.data().apps.forEach(app=>{
                oneProduct.apps.push({
                    app:app,
                    checked :false
                })
                })
                allProducts.push(oneProduct);
            })
            setProducts([...allProducts])
        }).catch(err => {
            console.log(err)
        })
    }, [])

    useEffect(() => {
        db.collection("codes").get().then(snapshot => {
            let allCodes =[];
            snapshot.docs.forEach(code => {
                code.checked=false;
                allCodes.push(code)
            })
            setCodes([...allCodes])
        })
    },[])

    // change search value
    const handleChangeSearchVal = e =>{
        setSearchedVal(e.target.value)
    }

    // handleChangePage
    const handleChangePage = e => {
    setPageNum(Number(e.target.value))
    }

    const toggleCodesSelection = (e)=>{
    let newSelectedCodes = [];
    let newCodes = codes;
    newCodes.forEach(code=>{
        code.checked = e.target.checked;
        if(e.target.checked){
          newSelectedCodes.push(code.id);
        }
    })
    setSelectedCodes([...newSelectedCodes])
    setCodes([...newCodes])
    }

    const handleSelectCode = (e,i,id)=>{
        let newSelectedCodes = selectedCodes;
        if(e.target.checked){
          newSelectedCodes.push(id);
        }else{
          newSelectedCodes = newSelectedCodes.filter((uid)=>{
            return uid !== id
          })
        }
        setSelectedCodes([...newSelectedCodes])
        let newCodes = codes;
        newCodes[i].checked = e.target.checked
        setCodes([...newCodes])
      }

    const showTheAddPop = ()=>{
        setShowCodePopup(true);
        let addingNewCode = {};
        addingNewCode.perfixCode  = 0;
        addingNewCode.lengthCode  = 0;
        addingNewCode.purchasedLocation  = "";
        addingNewCode.apps  = [];
        addingNewCode.notes  = "";
        setAddingNewCode({...addingNewCode});
    }
    const closeAddPop = () => {
        setShowCodePopup(false)
    }
    const handleAddingCodePerfix = (e) => {
        let newAddingCode = addingNewCode;
        newAddingCode.perfixCode = e.target.value;
        setAddingNewCode({...addingNewCode});
        console.log(addingNewCode)
    }
    const handleAddingCodeLength = (e) => {
        let newAddingCode = addingNewCode;
        newAddingCode.lengthCode = e.target.value;
        setAddingNewCode({...addingNewCode});
    }
    const handleAddingpurchasedLocation = (e) => {
        let newAddingCode = addingNewCode;
        newAddingCode.purchasedLocation = e.target.value;
        setAddingNewCode({...addingNewCode});
    }
    const handleSelectedApps = (e, pName, appName) => {
        let newAddingCode = addingNewCode;
        if(e.target.checked){
            newAddingCode.apps.push({productName:pName, appName: appName});
        }
        setAddingNewCode({...addingNewCode});
    }
    const handleNotesChange = e => {
        let newAddingCode = addingNewCode;
        newAddingCode.notes = e.target.value;
        setAddingNewCode({...addingNewCode});
    }

    let productApps = null;
    productApps = products.map((p,i) => {
        return (
            <Col md="3" key={i}>
                <p style={{marginTop: 20}}>{p.name}: </p>
                {
                    p.apps.map((app,j) => {
                        return (<div style={{display: "flex"}} key={j}>
                        <Form.Check className="mb-1 pl-0" style={{marginTop : -20}}>
                        <Form.Check.Label>
                            <Form.Check.Input
                            onChange={(e)=> handleSelectedApps(e, p.name, app.app.name)}
                            // checked={app.checked}
                            defaultValue={app.checked}
                            type="checkbox"
                            ></Form.Check.Input>
                            <span className="form-check-sign"></span>
                            </Form.Check.Label>
                        </Form.Check>
                        <span>{app.app.name}</span>
                        </div>)
                    })
                }
            </Col>
        )
    });

    let renderCodes = null;
    renderCodes = codes.map((code,i) => {
        if(!codes.length){
            return;
        }
        if(i == pageNum){
        return ;
        }
        if(code.data().code.includes(searchedVal)){
        if(i < pageNum){
        return (
            <tr key={i}>
            <td>
            <Form.Check className="mb-1 pl-0">
            <Form.Check.Label>
                <Form.Check.Input
                onChange={(e)=> handleSelectCode(e,i,code.id)}
                // checked={code.checked}
                    defaultValue=""
                    type="checkbox"
                ></Form.Check.Input>
                <span className="form-check-sign"></span>
                </Form.Check.Label>
            </Form.Check>
            </td>
                <td>{code.data().code}</td>
                <td>
                    {code.data().productActivated.map((p,j) => {
                        return <span key={j}>{p.productName}{p.appName},<br/></span>
                    })}
                </td>
                <td>{code.data().dateAdded}</td>
                <td>{code.data().purchasedLocation}</td>
                <td>
                <OverlayTrigger overlay={<Tooltip id="tooltip-422471719">Edit Task..</Tooltip>}>
                <Button
                    className="btn-simple btn-link p-1"
                    type="button"
                    variant="info"
                    // onClick = {()=> handleDeleteCodes(code.id,codes,i,setCodes)}
                ><i className="fas fa-edit"></i>
                </Button>
                </OverlayTrigger>
                <OverlayTrigger
                overlay={<Tooltip id="tooltip-829164576">Remove..</Tooltip>}>
                <Button
                    className="btn-simple btn-link p-1"
                    type="button"
                    variant="danger"
                    onClick = {() => handleDeleteCode(code.id,codes,i,setCodes)}
                ><i className="fas fa-times"></i>
                </Button>
                </OverlayTrigger> 
            </td>
        </tr>
        )
        }
        }
    })

  return (
    <>
      <Container fluid>
        <Row>
          <Col md="12">
            <Card className="strpied-tabled-with-hover table-bordered">
              <Card.Header>
                <Card.Title as="h4">Codes</Card.Title>
              </Card.Header>
              <Card.Body>
                <div className="searchContainer">
                  <InputGroup className="mb-3 inputSearch" >
                    <InputGroup.Prepend>
                      <InputGroup.Text id="basic-addon1" autoComplete="on" ><i className="nc-icon nc-zoom-split"></i></InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl
                      placeholder="Search"
                      aria-label="Search"
                      aria-describedby="basic-addon1"
                      value={searchedVal}
                      onChange={handleChangeSearchVal}
                    />
                  </InputGroup>
                </div>
                <div className="pagination">
                  <span>Admin Per Page : </span>
                  <select  onChange={e => handleChangePage(e)}>
                    <option vlaue="5">5</option>
                    <option vlaue="10">10</option>
                    <option vlaue="15">15</option>
                    <option vlaue="20">20</option>
                    <option vlaue="25">25</option>
                  </select>
                </div>
                <Table className="table-hover table-striped">
                  <thead>
                    <tr>
                      <th className="border-0">
                         <Form.Check  className="mb-1 pl-0" style={{display:"inline-block"}}>
                            <Form.Check.Label>
                              <Form.Check.Input
                                onChange={toggleCodesSelection}
                                // ref={allRef}
                                defaultValue="x"
                                type="checkbox"
                              ></Form.Check.Input>
                              <span className="form-check-sign"></span>
                            </Form.Check.Label>
                          </Form.Check>
                          <span>all</span>
                        </th>
                      <th className="border-0">Code</th>
                      <th className="border-0">Product Activated</th>
                      <th className="border-0">Date Added</th>
                      <th className="border-0">Place Of Purchase</th>
                      <th className="border-0">
                        <button onClick={showTheAddPop}>Add Code</button>
                        <button onClick={() => handleDeleteCodes(selectedCodes, setCodes)}>Delete Selected Codes</button>
                        <button onClick={() => console.log("Deleted Selected")}>Export Selected Code to CSV</button>
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                  {renderCodes}
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
            <div className={"thePopUp " + (showCodePopup ? "active" : "")} >
              <div className="thePopUpBody" style={{maxWidth: "770px "}}>
                    <div className="font-icon-detail closePopUp" onClick={closeAddPop}>
                    <i className="nc-icon nc-simple-remove"></i>
                    </div>
                    <h2 style={{marginBottom:50}}>Add Code</h2>
                <Tabs defaultActiveKey="SingleCode" id="uncontrolled-tab-example">
                    <Tab eventKey="SingleCode" title="Single Code" style={{paddingTop: 40}}>
                    <Form.Group>
                    <Row>
                        <Col md="4"><Row><Col xs="auto"><label>Code Perfix: </label></Col>
                    <Col>
                    <Form.Control
                            defaultValue={addingNewCode.perfixCode }
                            type="text"
                            onChange={handleAddingCodePerfix}
                        ></Form.Control></Col></Row></Col>
                        <Col md="4">
                        <Button
                            className="btn-fill pull-right"
                            type="submit"
                            variant="secondary"
                            onClick={()=> console.log("Add Code")}
                        >
                            Generate Random Code
                        </Button>
                        </Col>
                        <Col md="4">
                        <Row><Col xs="auto"><label>Code Length: </label></Col>
                    <Col>
                    <Form.Control
                            defaultValue={addingNewCode.lengthCode}
                            type="number"
                            onChange={handleAddingCodeLength}
                        ></Form.Control></Col></Row>
                        </Col>
                    </Row>
                    </Form.Group>
                    <Form.Group>
                <Row>
                    <Col xs="auto"><label>Purchased Location: </label></Col>
                    <Col xs="auto"><Form.Control
                            defaultValue={addingNewCode.purchasedLocation}
                            placeholder="Add Purchased Location"
                            type="text"
                            onChange={handleAddingpurchasedLocation}
                        ></Form.Control></Col>
                    </Row>
                    </Form.Group>
                    <hr />
                    <h5>Products To Activate</h5>
                    <Row>{productApps }</Row>
                    <hr style={{marginTop: 30}}/>
                    <Row>
                    <Col xs="12"><Form.Group controlId="exampleForm.ControlTextarea1">
                    <Form.Label>Notes: </Form.Label>
                        <Form.Control as="textarea" rows={3} defaultValue={addingNewCode.notes} onChange={handleNotesChange}/>
                    </Form.Group>
                    </Col>
                    </Row>
                <Row>
                <Col>
                <Button
                    className="btn-fill pull-right"
                    type="submit"
                    variant="info"
                    onClick={() => handleAddCode(addingNewCode, setCodes)}
                  >
                    Add Code
                  </Button>
                </Col>
            </Row>
                </Tab>
                <Tab eventKey="BulkCode" title="Bulk Code" style={{paddingTop: 40}}>
                    <Form.Group>
                    <Row><Col xs="auto"><label>Product Name</label></Col>
                    <Col>
                    <Form.Control
                            // defaultValue={addingNewProduct.name }
                            placeholder="Add Product Name"
                            type="text"
                            // onChange={handleAddingNameChange}
                        ></Form.Control></Col>
                    </Row>
                    </Form.Group>
                <Row>
                    <Col>
                    <Form.Group>
                    <Row><Col xs="auto"><label style={{marginBottom: 15}}>Product Description</label></Col>
                        <Col><Form.Control
                            // defaultValue={addingNewProduct.description }
                            placeholder="Add Product Name"
                            type="text"
                            // onChange={handleAddingDescriptionChange}
                        ></Form.Control></Col></Row>
                    </Form.Group>
                    </Col>
                </Row>
                <Row>
                    <Col>
                    <Form.Group>
                        <label>Unpurchased URL</label>
                        <Form.Control
                            // defaultValue={addingNewProduct.unpurchasedUrl}
                            placeholder="Add Product Page url"
                            type="text"
                            // onChange={handleAddingunpurchasedUrlChange}
                        ></Form.Control>
                    </Form.Group>
                    </Col>
                </Row>
                <Row>
                <Col>
                <Button
                    className="btn-fill pull-right"
                    type="submit"
                    variant="info"
                    // onClick={hadleAddCo/de}
                  >
                    Add Code
