import { db } from "../../firebase";


export const handleDeactiveApp = (user,product,theApp,setUser) => {
    let newUser = user;
    let editableProduct = newUser.products.find(p=>{
        return p.name == product.name;
    })
    
    newUser.products = newUser.products.filter(p=>{
        return p.name !== product.name;
    })

    editableProduct.apps.forEach(app => {
        if (app.name.name == theApp.name.name){
            app.deviceId = "";
        }
    });
    newUser.products.push(editableProduct);
    setUser({...newUser})

    return db.collection('users').doc(user.id).set(newUser) 
    
  }

  export const handleDeactiveAllApps = (user,product,setUser,callback) => {
    let newUser = user;
    let editableProduct = newUser.products.find(p=>{
        return p.name == product.name;
    })
    
    newUser.products = newUser.products.filter(p=>{
        return p.name !== product.name;
    })

    editableProduct.apps.forEach(app => {
            app.deviceId = "";
    });
    newUser.products.push(editableProduct);
    setUser({...newUser})
    callback()
    return db.collection('users').doc(user.id).set(newUser) 
    
  }

  export const handleInactiveCode = (code, email, id, theCodeObject,callback) => {
    db.collection("codes").get()
    .then(snapshot => {
    let activeCodeObject = {};
    snapshot.docs.forEach(oneCode => {
        if(Array.isArray(oneCode.data().code)){
            let activeCode = "";
            activeCode = oneCode.data().code.find(singleCode => singleCode==code);
            if(activeCode){
                activeCodeObject = oneCode.data() 
            }
        }else if(!Array.isArray(oneCode.data().code)){
            if(oneCode.data().code == code) {
                activeCodeObject = oneCode.data() 
            }else{
                return
            }
        }
    })
    db.collection("inactiveCodes").add({
        inactiveCode : code,
        activatedBy : email,
        dataAdded : new Date().toLocaleString(),
        placeOfPurchase: activeCodeObject.purchasedLocation,
        productActivated: activeCodeObject.productActivated,
        notes: activeCodeObject.notes,
    }).then(() => {
        console.log(theCodeObject.data().productActivated)
        // db.collection("codes").doc(theCodeObject.id).delete()
        updateAppsinUser(theCodeObject.data().productActivated,id,code)
        callback()
    })
    }).catch(err => {
        console.log(err)
    })
  }

  const updateAppsinUser = (productsActivated, id,code)=>{
    db.collection("users").doc(id).get()
    .then(snapshot=>{
        let allProducts=snapshot.data().products
        let theNewProduct = {};
        let productActivatedExist = {};

        productsActivated.forEach(p=>{
            allProducts.forEach((piu,i)=>{
                if(piu.name === p.productName){
                    piu.apps.forEach((app,j)=>{
                        if(app.name.name === p.appName){
                            allProducts[i].apps[j].purchased = true
                            app.purchased = true
                        }
                    })
                }
            })
        })










        snapshot.data().products.forEach((product) => {
            productActivatedExist = productsActivated.find(p => product.name == p.productName)
            // productActivatedExist.push(productActivatedExist)
            theNewProduct.name = product.name;
            theNewProduct.purchasedUrl= product.purchasedUrl;
            theNewProduct.unpurchasedUrl= product.unpurchasedUrl;
            theNewProduct.visibility= product.visibility;
            theNewProduct.description= product.description,
            theNewProduct.productIcon= product.productIcon,
            theNewProduct.id = product.id;
            theNewProduct.apps = [];
            let generatedApp   = {};
            product.apps.forEach(app => {
                generatedApp.active = productActivatedExist? productActivatedExist.appName == app.name.name ? true : app.active : app.active
                generatedApp.purchased= productActivatedExist?productActivatedExist.appName == app.name.name ? true : app.purchased :app.purchased
                generatedApp.credentials = app.credentials
                generatedApp.expireDate= app.expireDate
                generatedApp.timesAppLaunchedLastLogin = app.timesAppLaunchedLastLogin
                generatedApp.timesAppLaunchedPurchased = app.timesAppLaunchedPurchased
                generatedApp.lastLaunchDate = app.lastLaunchDate
                generatedApp.timesAppLaunched = app.timesAppLaunched
                generatedApp.deviceId = app.deviceId 
                generatedApp.activatedOn= productActivatedExist?productActivatedExist.appName == app.name.name ? new Date().toLocaleString() :app.activatedOn:app.activatedOn
                generatedApp.codeUsed= code
                generatedApp.DateofLastUse= app.DateofLastUse
                generatedApp.LaunchesSinceActive= app.LaunchesSinceActive
                generatedApp.name = app.name

                
            theNewProduct.apps.push(generatedApp)
            })
            // console.log("theNewProduct",theNewProduct)
            allProducts.push(theNewProduct)
        })
        console.log("allProducts",allProducts)
        // db.collection("users").doc(id).set({
        //     products: [...allProducts]
        // },{merge:true})
    })
}
