import { db, functions } from "../firebase";


const updateUser  = functions.httpsCallable("updateUser");

export const handleEditAdmin = (admin,setAdmins,callback)=>{
    let theAdmins = []
    // console.log(admin)
    return db.collection("admins").doc(admin.Uid).update({
        email:admin.email,
        firstName:admin.firstName,
        lastName:admin.lastName,
        }).then(()=>{
        db.collection("users").get().then(snapshot=>{
            snapshot.docs.forEach(user=>{
                user.checked = false;
                theAdmins.push(user)
            })
        })
        setAdmins([...theAdmins]);
        // updateUsers(admin)
        callback();
    })
    .catch(err=>{
        console.log("Err is ", err)
    })
}

export const handleDeleteAdmin = (uid,admins,i,setAdmins)=>{
    admins.splice(i, 1);
    setAdmins([...admins]);

    return db.collection("admins").doc(uid).delete()
    .catch(err=>{
        console.log("Err is ", err)
    })
}

export const handleDeleteAdmins = (ids) =>{
    return db.collection("admins").get().then((snapshot) => {
        snapshot.docs.forEach(admin => {
            ids.forEach(id => {
                console.log("id", id)
                db.collection("admins").doc(id).delete();
            })

        })
    }).catch(err=>{
        console.log("ERR",err);
    })
    console.log(ids)
}

