import React, {useRef,useEffect,useState } from "react";
import ChartistGraph from "react-chartist";
import { db } from "../firebase";
import {handleDeleteUser,handleDeleteUsers, handleUpdateUser} from './UsersAction'

// react-bootstrap components
import {
  Badge,
  Button,
  Card,
  Navbar,
  Nav,
  Table,
  Container,
  Row,
  Col,
  Form,
  OverlayTrigger,
  Tooltip
} from "react-bootstrap";
import Scroll from 'react-scroll';
import { Link, Element, Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll'

function Dashboard() {
  const allRef = useRef();
  const [users,setUsers] = useState([]) 
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [editableUser,setEditableUser] = useState({
    email:"",
    products:[]
  });

  var scroll    = Scroll.animateScroll;

  useEffect(() => {
    db.collection("users").get()
    .then(snapshot=>{
      let users = [];
      snapshot.docs.forEach(user=>{
        user.checked = false;
        users.push(user);
      })
      setUsers([...users]);

      let newEditableUser = {};
      newEditableUser.uid      = users[0].id;
      newEditableUser.email    = users[0].data().email;
      newEditableUser.products = users[0].data().products;
      setEditableUser({...newEditableUser});
    })
  },[]);


  const handleSelectUser = (e,i,id)=>{
    let newSelectedUsers = selectedUsers;
    if(e.target.checked){
      newSelectedUsers.push(id);
    }else{
      newSelectedUsers = newSelectedUsers.filter((uid)=>{
        return uid !== id
      })
    }
    setSelectedUsers([...newSelectedUsers])
    let newUsers = users;
    newUsers[i].checked = e.target.checked
    setUsers([...newUsers])
  }

  const toggleUsersSelection = (e)=>{
    let newSelectedUsers = [];
    let newUsers = users;
    newUsers.forEach(user=>{
      user.checked = e.target.checked;
      if(e.target.checked){
        newSelectedUsers.push(user.id);
      }
    })
    setSelectedUsers([...newSelectedUsers])
    setUsers([...newUsers])
  }

  const handleEditUser = (uid,i)=>{
    let newEditableUser = {};
    newEditableUser.uid      = users[i].id;
    newEditableUser.email    = users[i].data().email;
    newEditableUser.products = users[i].data().products;
    setEditableUser({...newEditableUser});
    scroll.scrollToBottom()

