import React,{useState, useEffect} from 'react'
import { useAuth } from "../contexts/AuthContext";
import { Link, useHistory } from "react-router-dom";
import { 
  Navbar, 
  Container, 
  Row, 
  Col, 
  InputGroup, 
  Button, 
  FormControl,
  Accordion,
  Card,
  Tabs,
  Tab
} from "react-bootstrap";
import { db } from "../../firebase";


export default function UserPortal() {

  const [theUser, setTheUser] = useState({});
  const [deactiveAppUser, setDeactiveAppUser] = useState([]);
  const [key, setKey] = useState('tab-0');

  useEffect(async () => {
    await db.collection("users").doc(currentUser.uid).get()
    .then(snapshot=>{
      console.log("usersSets", snapshot.id)
       setTheUser({...snapshot.data()});

    })
    console.log(currentUser.uid);

  }, [])

    const { currentUser, logout } = useAuth();
    const history = useHistory();

    const [error, setError] = useState("");

    async function handleLogout() {
        setError("");
    
        try {
          await logout();
          history.push("/login");
        } catch {
          setError("Failed to log out");
        }
      }
      // handleLogout();
    
    

    const deactiveAllApps = e => {
      // e.preventDefault();
      // theUser.products.map((p, i) => {
      //   p.apps.map((app,i) => {
      //     app.deviceId = ""
      //   })
      //   console.log(theUser)
      // })
    }

    const handleDeactiveApp = (user,p,app,setUsers) => {
      let theUsers = []
    // db.collection("users").doc(user.uid).docs(p.id).docs(app.id).update({
    //     deviceId: " "
    //     }).then(()=>{
    //         db.collection("users").get().then(snapshot=>{
    //             snapshot.docs.forEach(user=>{
    //                 theUsers.push(user);
    //             })
    //             setUsers([...theUsers]);
    //         })
    //     })
    // .catch(err=>{
    //     console.log("Err is ", err)
    // })
    }

    return (
      <div>
        <Container>
        <Navbar>
          <Navbar.Brand href="#home"></Navbar.Brand>
          <Navbar.Toggle />
          <Navbar.Collapse className="justify-content-end">
            <Navbar.Text>
            <button onClick={handleLogout}>logout</button>
            </Navbar.Text>
          </Navbar.Collapse>
        </Navbar>

        <div style={{paddingTop : 30, paddingBottom: 30}}>
          <div className="user_card">
            <strong className="mr-auto">Welcome </strong>
            <p>{theUser.userName} - </p>
            <p>{theUser.email}</p>
          </div>
          <Accordion defaultActiveKey="0" style={{marginTop: 30, marginBottom: 30,}}>
            <Card>
              <Card.Header style={{paddingBottom: 10}}>
                <Accordion.Toggle as={Button} variant="link" eventKey="1">
                  Reset Passowrd
                </Accordion.Toggle>
              </Card.Header>
              <Accordion.Collapse eventKey="1">
                <Card.Body>
                <ul>
                  <li>instructions</li>
                  <li>instructions</li>
                  <li>instructions</li>
                  <li>instructions</li>
                </ul>
                </Card.Body>
              </Accordion.Collapse>
            </Card>
          </Accordion>
          <div className="user_card">
            <strong className="mr-auto">Activate a Code: </strong>
            <form onSubmit="">
            <InputGroup className="mb-3">
              <FormControl
                placeholder="Your Code"
                aria-label="Your Code"
                aria-describedby="Your Code"
              />
              <InputGroup.Append>
                <Button variant="secondary" type="submit">Activate Code</Button>  
              </InputGroup.Append>
            </InputGroup>
            </form>
          </div>
         
            <Tabs id="controlled-tab-example" activeKey={key} onSelect={(k) => setKey(k)} >
              {theUser.products.map((p, j) => (
                <Tab eventKey='tab-${i}' title={p.name} key={j}>
                <div className="productUser" style={{borderColor: p.purchased ? "#218838" : "#AAA"}}>
                  <div style={{display: 'flex', justifyContent: "space-between"}}>
                    <h4>{p.name}</h4>
                    <Button style={{height:50, fontSize: 12}} variant="danger" onClick={deactiveAllApps}>Deactive all apps</Button>
                  </div>
                  <a href="">{p.purchased ? p.purchasedUrl : p.unpurchasedUrl}</a>
                  <div >
                    {p.apps.map((app,i) => {
                      return <div className="apps">
                                <div style={{display: "flex", justifyContent: "space-between", marginBottom: 20}}><h6>{app.name.name}</h6>
                                <Button className="ml-auto" style={{padding:5, fontSize: 12}} variant="danger" onClick={e => handleDeactiveApp(e,theUser,p, app, setUsers)}>Deactive app</Button></div>
                                <p><strong>Expired Date : </strong>{app.expireDate}</p>
                                <p><strong>Last Launched Date : </strong>{app.lastLaunchDate}</p>
                                <p><strong>Times App Launched : </strong>{app.timesAppLaunched}</p>
                                <p><strong>Times App Launched LastLogin: </strong>{app.timesAppLaunchedLastLogin}</p>
                                <p><strong>Times App Launched Purchased : </strong>{app.timesAppLaunchedPurchased}</p>
                            </div>
                    })}
                  </div>
                </div>
                </Tab>
              ))}
            </Tabs> {console.log(theUser.products)}
        </div>
       
        </Container>
      </div>
    )
}
