import React, { Component, Fragment } from "react";
import PropTypes from "prop-types";
import {
    Container,
    Row,
    Col,
  } from "react-bootstrap";
import SelectBox from "../components/selectBox"

const LEFT_PAGE = "LEFT";
const RIGHT_PAGE = "RIGHT";



class Pagination extends Component {
  constructor(props) {
    super(props);
    const { totalRecords = null, pageLimit = 30, pageNeighbours = 0 } = props;

    this.pageLimit = typeof pageLimit === "number" ? pageLimit : 15;
    this.totalRecords = typeof totalRecords === "number" ? totalRecords : 0;

    this.pageNeighbours =
      typeof pageNeighbours === "number"
        ? Math.max(0, Math.min(pageNeighbours, 2))
        : 0;

    this.totalPages = Math.ceil(this.totalRecords / this.pageLimit);

    this.state = { currentPage: 1 };
  }

  componentDidMount() {
    this.gotoPage(1);
  }

  gotoPage = page => {
    const { onPageChanged = f => f } = this.props;

    const currentPage = Math.max(0, Math.min(page, this.totalPages));

    const paginationData = {
      currentPage,
      totalPages: this.totalPages,
      pageLimit: this.pageLimit,
      totalRecords: this.totalRecords
    };

    this.setState({ currentPage }, () => onPageChanged(paginationData));
  };

  handleClick = (page, evt) => {
    evt.preventDefault();
    this.gotoPage(page);
  };

  handleMoveLeft = evt => {
    evt.preventDefault();
    this.gotoPage(this.state.currentPage - this.pageNeighbours );
  };

  handleMoveRight = evt => {
    evt.preventDefault();
    this.gotoPage(this.state.currentPage + this.pageNeighbours );
  };

  fetchPageNumbers = () => {
    const totalPages = this.totalPages;
    let pages = [];
    for (let i = 1; i <= totalPages; i++) {
      pages.push(i);
    }

    return [...pages];
  };

  render() {
    if (!this.totalRecords) return null;

    if (this.totalPages === 1) return null;

    const { currentPage } = this.state;
    const pages = this.fetchPageNumbers();
    let thePages = []
    const renderPages = pages.map(page => {
        thePages.push({
            value:  page,
            onClick : e => this.handleClick(page, e),
            class : currentPage === page ? " active" : ""
        })
    })

    return (
      <Row>
          <Col md={4}><button className="page-item">
            <a
                className="page-link"
                href="#"
                aria-label="Previous"
                onClick={this.handleMoveLeft}
            >
                Previous Page
            </a>
            </button></Col>
            <Col md={4}>
            <div className="d-flex flex-row align-items-center">
              {currentPage && (
                <span className="current-page d-inline-block h-100 pl-4 text-secondary">
                  Page <span className="font-weight-bold">{currentPage}</span> /{" "}
                  <span className="font-weight-bold">{this.totalPages}</span>
                </span>
              )}
            </div>
                <SelectBox currentPage={currentPage}
                items={pages.map(page => {
                    return {
                        value:  page,
                        onClick : e => this.handleClick(page, e)
                    }
                })} />
            </Col>
            <Col md={4}><button className="page-item">
            <a
                className="page-link"
                href="#"
                aria-label="Next"
                onClick={this.handleMoveRight}
            >
                Next Page
            </a>
            </button></Col>
      </Row>
    );
  }
}

Pagination.propTypes = {
  totalRecords: PropTypes.number.isRequired,
  pageLimit: PropTypes.number,
  pageNeighbours: PropTypes.number,
  onPageChanged: PropTypes.func
};

export default Pagination;
