import React, { useRef, useState } from "react";
import { Form, Button, Card, Alert } from "react-bootstrap";
import { useAuth } from "./contexts/AuthContext"
import { Link, useHistory } from "react-router-dom";
import { db } from "../firebase";

export default function Signup() {
  const emailRef = useRef();
  const userNameRef = useRef();
  const userCode = useRef();
  const passwordRef = useRef();
  const passwordConfirmRef = useRef();
  const { signup } = useAuth();
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const history = useHistory();
  const [name, setName] = useState('') 
  const [code, setCode] = useState('') 

  async function handleSubmit(e,userRef,codeRef,passRef) {
    e.preventDefault();

    if (passwordRef.current.value !== passwordConfirmRef.current.value) {
      return setError("Passwords do not match");
    }

    setError("");
    setLoading(true);
    await signup(emailRef.current.value, passwordRef.current.value)
    .then(function(cred){
      db.collection("products").get().then(snapshot=>{
      let theUser = {};
      theUser.email= cred.user.email;
      // theUser.password= cred.user.password;
      theUser.userName= userRef;
      theUser.userCode= codeRef;
      theUser.creationDate=cred.user.metadata.creationTime;
      theUser.lastLoginDate=cred.user.metadata.lastSignInTime;
      theUser.products = [];

      snapshot.docs.forEach(p=>{
        let oneProduct = {};
        oneProduct.name = p.data().name;
        oneProduct.url = p.data().url;
        // oneProduct.visibility = p.data().visibility;
        oneProduct.apps = [];
        oneProduct.comments = "comment";
        oneProduct.purchased = false;
        oneProduct.datePurchased = "22/1/2021";
        p.data().apps.forEach(app=>{
          oneProduct.apps.push({
            name:app,
            active:false,
            purchased: false,
            expireDate: "30/3/2021",
            credentials : false,
            timesAppLaunchedLastLogin : 3,
            timesAppLaunchedPurchased : 4,
            lastLaunchDate : "23-12-2020",
            timesAppLaunched : 7,
            deviceId : "1.23.456.5"
          })
        })
        theUser.products.push(oneProduct);
        console.log("the user is ", theUser)
      })

      return db.collection('users').doc(cred.user.uid).set(theUser) 
      })
    }).then(()=>{
      history.push("/");
    }) 
    .catch(err=>{
      setError("Failed to create an account");
    })

    setLoading(false);
  }

  return (
    <>
    <div className="w-100" style={{ maxWidth: "400px",margin:"50px auto" }}>
      <Card>
        <Card.Body>
          <h2 className="text-center mb-4">Sign Up</h2>{console.log("password",passwordRef.current.value)}
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={(e)=>handleSubmit(e,userNameRef.current.value,userCode.current.value)}>
            <Form.Group id="email">
              <Form.Label>Email</Form.Label>
              <Form.Control type="email" ref={emailRef} required />
            </Form.Group>
            <Form.Group id="user-name">
              <Form.Label>User Name</Form.Label>
              <Form.Control type="text" ref={userNameRef} defaultValue={name} required />
            </Form.Group>
            <Form.Group id="user-code">
              <Form.Label>User Code</Form.Label>
              <Form.Control type="text" ref={userCode} defaultValue={code} onChange={(e) => setCode(e.target.value)} required />
            </Form.Group>
            <Form.Group id="password">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" ref={passwordRef} required />
            </Form.Group>
            <Form.Group id="password-confirm">
              <Form.Label>Password Confirmation</Form.Label>
              <Form.Control type="password" ref={passwordConfirmRef} required />
            </Form.Group>
            <Button disabled={loading} className="w-100" type="submit">
              Sign Up
            </Button>
          </Form>
        </Card.Body>
      </Card>
      <div className="w-100 text-center mt-2">
        Already have an account? <Link to="/login">Log In</Link>
      </div>
      </div>
    </>
  );
}
