import { functions, db } from "../firebase";
import { useAuth } from "../components/contexts/AuthContext"
const deleteUser  = functions.httpsCallable("deleteUser");
const deleteUsers = functions.httpsCallable("deleteUsers");
const updateUser  = functions.httpsCallable("updateUser");

export const handleUpdateUser = (user,setUsers,callback)=>{
    let theUsers = []
    return updateUser(user)
    .then(()=>{
        db.collection("users").doc(user.uid).update({
            email:user.email,
            password:user.password,
            userName:user.userName,
            userCode:user.userCode,
            products:user.products
        }).then(()=>{
            db.collection("users").get().then(snapshot=>{
                snapshot.docs.forEach(user=>{
                    user.checked = false;
                    user.purchased = false;
                    theUsers.push(user);
                })
                setUsers([...theUsers]);
            })
        })
    })
    .catch(err=>{
        console.log("Err is ", err)
    })
}

export const handleDeleteUser = (uid,users,i,setUsers)=>{
    users.splice(i, 1);
    setUsers([...users]);

    return deleteUser({uid:uid})
    .then(()=>{
        db.collection("users").doc(uid).delete();

    })
    .catch(err=>{
        console.log("Err is ", err)
    })
}
export const handleDeleteUsers = (ids) =>{
    return deleteUsers(ids)
    .then(res=>{
        console.log("res after deleting users",res);
        ids.forEach(uid => {
            db.collection("users").doc(uid).delete();
        });
    }).catch(err=>{
        console.log("ERR",err);
    })
}

export const handleSubmit = async(e,firstNameRef, lastNameRef,codeRef) => {
     console.log("work")
    const { signup } = useAuth();
    e.preventDefault();

    setError("");
    setLoading(true);
    await signup(emailRef.current.value, passwordRef.current.value)
    .then(function(cred){
      db.collection("products").get().then(snapshot=>{
      let theUser = {};
      theUser.email= cred.user.email;
      // theUser.password= '';
      theUser.firstName= firstNameRef;
      theUser.lastName= lastNameRef;
      theUser.userCode= codeRef;
      theUser.creationDate=cred.user.metadata.creationTime;
      theUser.lastLoginDate=cred.user.metadata.lastSignInTime;
      theUser.products = [];
        
      snapshot.docs.forEach(p=>{
        let oneProduct = {};
        oneProduct.name = p.data().name;
        oneProduct.visibility = p.data().visibility;
        oneProduct.apps = [];
        oneProduct.comments = "comment";
        oneProduct.purchased = false;
        oneProduct.purchasedUrl = p.data().purchasedUrl;
        oneProduct.unpurchasedUrl = p.data().unpurchasedUrl;
        oneProduct.datePurchased = "22/1/2021";
        p.data().apps.forEach(app=>{
          oneProduct.apps.push({
            name:app,
            activeUrl:app.activeUrl,
            inActiveUrl:app.inActiveUrl,
            active:false,
            purchased: false,
            credentials : false,
            expireDate: "30/3/2021",
            timesAppLaunchedLastLogin : 3,
            timesAppLaunchedPurchased : 4,
            lastLaunchDate : "23-12-2020",
            launchesSinceActive :"8-6-2020",
            dateOfLastUse: "8-6-2020",
            activatedOn: "8-6-2020",
            timesAppLaunched : 7,
            codeUsed: "9877",
            deviceId : "1.23.456.5"
          })
        })
        theUser.products.push(oneProduct);
        console.log("the user is ", theUser.products)
      })

      return db.collection('users').doc(cred.user.uid).set(theUser) 
      })
    }) 
    .catch(err=>{
      setError("Failed to create an account");
    })

    setLoading(false);
  }


