import {db} from '../firebase';
import {crypto} from 'crypto'


export const hadleAddCode = (code) => {
    let cryptoCode = null;
    cryptoCode = code.perfixCode+crypto.randomBytes(code.lengthCode).toString("hex")
    // db.collection("codes").add({
