import React, {useState, useEffect, useRef} from 'react';
import {
    Card,
    Table,
    Container,
    Row,
    Col,
    Form,
    Button ,
    InputGroup, 
    FormControl,
    OverlayTrigger,
    Tooltip,
    Accordion
  } from "react-bootstrap";
  import { db, functions } from "../firebase";
  import {handleEditAdmin} from './adminAction'

const CreateAdmin = () => {

  const allRef = useRef();
  const [admins, setAdmins] = useState([]);
  const [users, setUsers] = useState([]);
  const [user, setUser] = useState(null);
  const [editableAdmin, setEditableAdmin] = useState({email:"", firstName:"", lastName: ""});
  const [modalShow, setModalShow] = useState(false)
  
const addAdminRole = functions.httpsCallable("addAdminRole");

  useEffect(() => {
    db.collection("users").get()
    .then(snapshot=>{
      let users = [];
      snapshot.docs.forEach(user=>{
        users.push(user);
      })
      setUsers([...users]);
  })
  }, [])

  const handleAddNewAdmin = (e,setAdmins) => {
    
    const adminUser = users.find((user) =>  user.data().email == e.target.value);
    // console.log(adminUser)
        let theAdmins = [];
    addAdminRole(e.target.value).then(() => {
      db.collection("admins").add({
        firstName : adminUser.data().firstName,
        lastName : adminUser.data().lastName,
        email : adminUser.data().email,
        dateAdded : new Date().toLocaleDateString()
      })
      .then(users => {
        users.forEach(user => {
          user.checked = false;
          theAdmins.push(user)
        })
        setAdmins([...theAdmins])
      })
      .catch((error) => {
        console.error("Error writing document: ", error);
    });
      console.log(adminUser.data())
    })
  }

  //get admins

  useEffect(() => {
    db.collection("admins").get()
    .then(snapshot=>{
      let theAdmins = [];
      snapshot.docs.forEach(admin=>{
        theAdmins.push(admin);
      })
      setAdmins([...theAdmins]);
    })
  },[])

 //onClickBodyPopup
 const onClickBodyPopup= e => {
  e.preventDefault();
  e.stopPropagation()
  }

  const closeEditPop = ()=>{
    setModalShow(false);
  }

  const handleEditAdmin = (email,i)=>{
    let newEditableAdmin = {};
    newEditableAdmin.email    = users[i].data().email;
    newEditableAdmin.firstName    = users[i].data().firstName;
    newEditableAdmin.lastName    = users[i].data().lastName;
    setEditableAdmin({...neweditableAdmin});
    setModalShow(true)
  }

  const hanldeChangeEmailForeditableAdmin = (e)=>{
    let newEditableAdmin = editableAdmin;
    newEditableAdmin.email = e.target.value;
    setEditableAdmin({...newEditableAdmin})
  }

  const hanldeChangeFirstNameForeditableAdmin = (e)=>{
    let newEditableAdmin = editableAdmin;
    newEditableAdmin.firstName = e.target.value;
    setEditableAdmin({...newEditableAdmin})
  }

  const hanldeChangeLastNameForeditableAdmin = (e)=>{
    let newEditableAdmin = editableAdmin;
    newEditableAdmin.lastName = e.target.value;
    setEditableAdmin({...newEditableAdmin})
  }

  const AdminEditPopup = (props) => {
    return (
      <div className={"thePopUp " + (modalShow ? "active" : "")} onClick={closeEditPop}>
          <div className="thePopUpBody" onClick={onClickBodyPopup}>
            <div className="font-icon-detail closePopUp" onClick={closeEditPop}>
              <i className="nc-icon nc-simple-remove"></i>
          </div>
        <Modal.Header >
          <Modal.Title >
            <h4 className="card-title">Edit Admin</h4>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="userDetails">
            <Form.Group>
              <label>Email</label>
              <Form.Control
                  defaultValue={editableAdmin.email}
                  placeholder="Email"
                  type="text"
                  onChange={hanldeChangeEmailForeditableAdmin}
              ></Form.Control>
            </Form.Group>
            <Form.Group>
              <label>First Name</label>
              <Form.Control
                  defaultValue={editableAdmin.firstName}
                  placeholder="First Name"
                  type="text"
                  onChange={hanldeChangeFirstNameForEditableAdmin}
              ></Form.Control>
            </Form.Group>
            <Form.Group>
                <label>Last Name</label>
                <Form.Control
                    defaultValue={editableAdmin.lastName}
                    placeholder="Last Name"
                    type="text"
                    onChange={hanldeChangeLastNameForEditableAdmin}
                ></Form.Control>
            </Form.Group>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Close</Button>
          <Button onClick={() => handleEditAdmin(editableAdmin, setAdmins)}>Save</Button>
        </Modal.Footer>
          </div>
      </div>
    );
  }
    
  let renderAllAdmins = null;
  renderAllAdmins = admins.map((admin, i) => (
    <tbody>
    <tr>
      <td>
        <Form.Check className="mb-1 pl-0">
        <Form.Check.Label>
          <Form.Check.Input
            // onChange={(e)=>{handleSelectAdmin(e,i,user.id)}}
            checked={admin.checked}
              defaultValue=""
              type="checkbox"
            ></Form.Check.Input>
            <span className="form-check-sign"></span>
          </Form.Check.Label>
        </Form.Check>
      </td>
    <td>{admin.data().firstName}</td>
    <td>{admin.data().lastName}</td>
    <td>{admin.data().email}</td>
    <td>{admin.data().dateAdded}</td>
    <td>
      <OverlayTrigger
        overlay={
          <Tooltip id="tooltip-422471719">
            Edit Task..
          </Tooltip>
        }
      >
        <Button
          onClick={()=>{handleEditAdmin(admin.data().email,i)}}
          className="btn-simple btn-link p-1"
          type="button"
          variant="info"
        >
          <i className="fas fa-edit"></i>
        </Button>
      </OverlayTrigger>
      <OverlayTrigger
        overlay={
          <Tooltip id="tooltip-829164576">Remove..</Tooltip>
        }
      >
      <Button
        onClick={()=>handleDeleteUser(user.id,users,i,setUsers)}
        className="btn-simple btn-link p-1"
        type="button"
        variant="danger"
      >
        <i className="fas fa-times"></i>
      </Button>
      </OverlayTrigger>
    </td>
    </tr>
    </tbody>
  ))

    return (
        <div>
        <Container fluid>
        <Row> 
          <Col>
            <Card className="card-tasks">
              <Card.Header>
                <Card.Title as="h4" style={{marginBottom: 30}}>Admin Accounts</Card.Title>
              </Card.Header>
              <Card.Body>
                <div className="searchContainer">
                  <InputGroup className="mb-3 inputSearch" >
                    <InputGroup.Prepend>
                      <InputGroup.Text id="basic-addon1" autoComplete="on" ><i className="nc-icon nc-zoom-split"></i></InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl
                      placeholder="Search"
                      aria-label="Search"
                      aria-describedby="basic-addon1"
                      // value={searchedVal}
                      // onChange={handleChangeSearchVal}
                    />
                  </InputGroup>
                </div>
                <div className="pagination">
                  <span>Admin Per Page : </span>
                  <select >
                    <option vlaue="25">25</option>
                    <option vlaue="50">50</option>
                    <option vlaue="100">100</option>
                  </select>
                </div>
                  <Accordion defaultActiveKey="0">
                    <Card>
                      <Card.Header>
                        <Accordion.Toggle as={Button} variant="link" eventKey="1">
                          Add New Account
                        </Accordion.Toggle>
                      </Card.Header>
                      <Accordion.Collapse eventKey="1">
                        <Card.Body>
                        <div className="pagination">
                          <span>Admin User : </span>
                          <select onChange={e => handleAddNewAdmin(e,setAdmins)}>
                            {
                              users.map((user, i) => (
                                <option key={i} value={user.data().email}>{user.data().email}</option>
                              ))
                            }
                          </select>
                        </div>
                        </Card.Body>
                      </Accordion.Collapse>
                    </Card>
                  </Accordion>
              <Table className="table-hover table-striped table-bordered">
                  <thead>
                    <tr>
                    <th className="border-0">
                      <Form.Check  className="mb-1 pl-0" style={{display:"inline-block"}}>
                        <Form.Check.Label>
                          <Form.Check.Input
                            // onChange={toggleUsersSelection}
                            ref={allRef}
                            defaultValue="x"
                            type="checkbox"
                          ></Form.Check.Input>
                          <span className="form-check-sign"></span>
                        </Form.Check.Label>
                      </Form.Check>
                      <span>all</span>
                      </th>
                      <th className="border-0">First Name</th>
                      <th className="border-0">Last Name</th>
                      <th className="border-0">Email</th>
                      <th className="border-0">Date Added</th>

                      <th className="border-0">
                        <button onClick={()=> console.log("Delete Selected Accounts")}>Delete Selected Accounts</button>
                      </th>
                    </tr>
                  </thead>
                    {renderAllAdmins}
                </Table>
              </Card.Body>
              <Card.Footer>
              </Card.Footer>
            </Card>
          </Col>
        </Row>
        <AdminEditPopup
          show={modalShow}
          onHide={() => setModalShow(false)}
        />
        </Container>
        </div>
    )
}

export default CreateAdmin
