import React,{useState, useEffect} from 'react'
import { useAuth } from "../contexts/AuthContext";
import { Link, useHistory } from "react-router-dom";
import { Navbar, Container, Row, Col, InputGroup, Button, FormControl} from "react-bootstrap";
import { db } from "../../firebase";


export default function UserPortal() {

  const [users, setUsers] = useState([]);

  useEffect(() => {
    db.collection("users").get()
    .then(snapshot=>{
      setUsers([...snapshot.docs]);
    })
  }, [])

    const { currentUser, logout } = useAuth();
    const history = useHistory();

    const [error, setError] = useState("");

    async function handleLogout() {
        setError("");
    
        try {
          await logout();
          history.push("/login");
        } catch {
          setError("Failed to log out");
        }
      }
      // handleLogout();
    
    const userCurrent = users.find(user => user.data().email == currentUser.email)
    
    console.log(userCurrent.data())

    const deactiveAllApps = e => {
      e.preventDefault();
      userCurrent.data().products.map((p, i) => {
        p.apps.map((app,i) => {
          app.deviceId = ""
        })
        console.log(userCurrent.data())
      })

    }

    const deactiveOneApp = e => {
      console.log("clicked")
    }

    return (
        <div>
          <Container>
          <Navbar>
            <Navbar.Brand href="#home"></Navbar.Brand>
            <Navbar.Toggle />
            <Navbar.Collapse className="justify-content-end">
              <Navbar.Text>
              <button onClick={handleLogout}>logout</button>
              </Navbar.Text>
            </Navbar.Collapse>
          </Navbar>
          <div style={{paddingTop : 30, paddingBottom: 30}}>
            <Row>
              <Col>
                <div className="user_card">
                  <strong className="mr-auto">Email: </strong>
                  <p>{userCurrent.data().email}</p>
                </div>
              </Col>
              <Col>
                <div className="user_card">
                  <strong className="mr-auto">Name: </strong>
                  <p>{userCurrent.data().userName}</p>
                </div>
              </Col>
            </Row>
            <div className="user_card">
              <h4 className="mr-auto">Reset Passowrd: </h4>
              <ul>
                <li>instructions</li>
                <li>instructions</li>
                <li>instructions</li>
                <li>instructions</li>
              </ul>
            </div>
            
            <div className="user_card">
              <strong className="mr-auto">Code: </strong>
              <form onSubmit="">
              <InputGroup className="mb-3">
                <FormControl
                  placeholder="Your Code"
                  aria-label="Your Code"
                  aria-describedby="Your Code"
                />
                <InputGroup.Append>
                  <Button variant="secondary" type="submit">Submit</Button>  
                </InputGroup.Append>
              </InputGroup>
              </form>
            </div>
            <Row>
              {userCurrent.data().products.map((p, i) => (
                <Col md={6} key={i}>
                  <div className="productUser" style={{borderColor: p.purchased ? "#218838" : "#AAA"}}>
                    <div style={{display: 'flex'}}>
                      <h4>{p.name}</h4>
                      <Button className="mr-auto" variant="danger" onClick={deactiveAllApps}>Deactive all apps</Button>
                    </div>
                    <a href="">{p.purchased ? p.purchasedUrl : p.unpurchasedUrl}</a>
                    <div class="apps">
                      {p.apps.map((app,i) => {
                        return <div style={{display: 'flex'}}><h6>{app.name.name}</h6><Button className="mr-auto" variant="danger" onClick={deactiveOneApp}>Deactive all apps</Button></div>
                      })}
                    </div>
                  </div>
                </Col>  
              ))}
            </Row>
          </div>
          </Container>
        </div>
    )
}
