import React, { useEffect, useState }   from "react";
import {db} from '../firebase';
import {
    Card,
    Table,
    Container,
    Row,
    Col,
    Form,
    OverlayTrigger,
    Tooltip,
    Button 

  } from "react-bootstrap";

  import {handleDeleteProduct,handleEditProduct, handleAddNewProduct} from './ProductsActions';



// react-bootstrap components

const addProduct = ()=>{
    const [products,setProducts] = useState([]);
    const [showEditPop,setShowEditPop] = useState(false);
    const [showAddPop,setShowAddPop] = useState(false);
    const [editableProduct,setEditableProduct] = useState({});
    const [addingNewProduct,setAddingNewProduct] = useState({});
    const [showApp, setShowApp] = useState(false)
    const [createAppComponent, setCreateAppComponent] = useState([])


    useEffect(() => {
        db.collection("products").get()
            .then(snapshot=>{
            // console.log("snapshot is ",snapshot.docs[0].data().apps);
            setProducts([...snapshot.docs]);
        })
    }, [])

    const closeEditPop = ()=>{
        setShowEditPop(false);
    }

    const closeAddPop = ()=>{
        setShowAddPop(false);
    }

    const showTheAddPop = ()=>{
        setShowAddPop(true);
        let addingNewProduct = {};
        addingNewProduct.name  = "";
        addingNewProduct.visibility  = false;
        addingNewProduct.unpurchasedUrl  = "";
        addingNewProduct.purchasedUrl  = "";
        addingNewProduct.apps  = [{name:"", url: ""}];
        setAddingNewProduct({...addingNewProduct});
    }

    const resetEditableProduct = (pid,i)=>{
        let newEditableProduct = {};
        newEditableProduct.id = pid;
        newEditableProduct.name  = products[i].data().name;
        newEditableProduct.apps  = products[i].data().apps;
        newEditableProduct.visibility  = products[i].data().visibility;
        newEditableProduct.unpurchasedUrl  = products[i].data().unpurchasedUrl;
        newEditableProduct.purchasedUrl  = products[i].data().purchasedUrl;
        newEditableProduct.index = i;
        setEditableProduct({...newEditableProduct});
        setShowEditPop(true)
    }

    const hanldeEditableNameChange = (e)=>{
        let newEditableProduct = editableProduct
        newEditableProduct.name = e.target.value
        setEditableProduct({...newEditableProduct})
    }

    const hanldeEditableAppsChange = (e) =>{
      let newEditableProduct = editableProduct;
      let oldApp = newEditableProduct.apps;
      console.log("oldApp", oldApp);
      let newApp = e.target.value.split(",");
      console.log("newApp", newApp);
      let tempArr =[]
      newApp.map((app,i) => {
        if (oldApp[i].name != app){
          tempArr.push({name: app, url: oldApp[i].url})
        }
      })
      console.log("tempArr", tempArr)
      newEditableProduct.apps = tempArr
      setEditableProduct({...newEditableProduct})
    }

    const handleEditingAppUrlChange = (e,appIndex) => {
      let newEditableProduct = editableProduct;
      let newEditingNewProduct = editableProduct;
      let apps = newEditableProduct.apps;
      apps[appIndex].url =e.target.value;
      newEditingNewProduct.apps = apps
      setAddingNewProduct({...newEditingNewProduct});
    }

    const hanldeEditableunpurchasedUrlChange = (e) => {
      let newEditableProduct = editableProduct;
        newEditableProduct.unpurchasedUrl = e.target.value
        setEditableProduct({...newEditableProduct})
    }
    const hanldeEditablepurchasedUrlChange = (e) => {
      let newEditableProduct = editableProduct;
        newEditableProduct.purchasedUrl = e.target.value
        setEditableProduct({...newEditableProduct})
    }

    const toggleShowEditing = (e) => {
      let newEditableProduct = editableProduct;
      newEditableProduct.visibility = e.target.checked;
      setEditableProduct({...newEditableProduct})
    }

    const handleAddingNameChange = (e)=>{
      let newAddingProduct = addingNewProduct;
      newAddingProduct.name = e.target.value
      setAddingNewProduct({...newAddingProduct})
    }

    const handleAddingAppsChange = (e, appIndex) =>{
      let newAddingProduct = addingNewProduct;
      newAddingProduct.apps[appIndex].name= e.target.value;
      setAddingNewProduct({...newAddingProduct})
    }

    const handleAddingAppUrlChange = (e,appIndex) => {
      let newAddingProduct = addingNewProduct;
      let newAddingNewProduct = addingNewProduct;
      let apps = newAddingProduct.apps;
      apps[appIndex].url =e.target.value;
      newAddingNewProduct.apps = apps
      setAddingNewProduct({...newAddingNewProduct});
    }

    const handleAddingunpurchasedUrlChange = (e) =>{
      let newAddingProduct = addingNewProduct;
      newAddingProduct.unpurchasedUrl = e.target.value
      setAddingNewProduct({...newAddingProduct})
    }

    const handleAddingpurchasedUrlChange = (e) =>{
      let newAddingProduct = addingNewProduct;
      newAddingProduct.purchasedUrl = e.target.value
      setAddingNewProduct({...newAddingProduct})
    }


    // check app box
    const toggleShowAdding = (e) => {
      let newAddingProduct = addingNewProduct;
      newAddingProduct.visibility = e.target.checked;
      setAddingNewProduct({...newAddingProduct})
    }

    let editApps = [];
    let editableProductApps = null

    let productsList = null; 
    productsList = products.map((product,i)=>{
        if(!products.length){
          return;
        }    

        return <tr key={product.id}>
        <td>{product.data().name}</td>
        <td>{product.data().name}</td>
        <td>{product.data().visibility ? "yes" : "No"}</td>
        <td>{product.data().purchasedUrl}</td>
        <td>{product.data().unpurchasedUrl}</td>
        <td></td>
        <td>

        <OverlayTrigger
              overlay={
                <Tooltip id="tooltip-422471719">
                  Edit Task..
                </Tooltip>
              }
            >
              <Button
                className="btn-simple btn-link p-1"
                type="button"
                variant="info"
                onClick = {()=>{resetEditableProduct(product.id,i)}}
              >
                <i className="fas fa-edit"></i>
              </Button>
            </OverlayTrigger>
        <OverlayTrigger
          overlay={
            <Tooltip id="tooltip-829164576">Remove..</Tooltip>
          }
        >
          <Button
            className="btn-simple btn-link p-1"
            type="button"
            variant="danger"
            onClick = {()=>{handleDeleteProduct(product,setProducts)}}
          >
            <i className="fas fa-times"></i>
          </Button>
        </OverlayTrigger>
        </td>
      </tr>
      })

    const AppComponent = (props) => {
      return (
        <Row >
              <Col>
              <Form.Group>
                  <label>Product Apps</label>
                  <Form.Control
                    defaultValue={props.name}
                    placeholder="Add Product Apps"
                    type="text"
                    onChange={props.handleAddingAppsChange}
                  ></Form.Control>
              </Form.Group>
              </Col>
                <Col>
                <Form.Group>
                  <label>App URL</label>
                  <Form.Control
                    defaultValue={props.url}
                    placeholder="Add Product Page url"
                    type="text"
                    onChange={props.handleAddingAppUrlChange}
                  > 
                  </Form.Control>
                </Form.Group>
              </Col>
              <Button variant="success">+</Button>
              <Button variant="danger">-</Button>
            </Row>
      )
    }

    const addComponent = (e, name, handleAddingAppsChange, url, handleAddingAppUrlChange)=> {
      setCreateAppComponent ([...createAppComponent, <AppComponent name={name} handleAddingAppsChange={handleAddingAppsChange} url={url} handleAddingAppUrlChang={handleAddingAppUrlChange}/>])
    }
    return (
        <>
      <Container fluid>
        <Row> 
          <Col>
            <Card className="card-tasks">
              <Card.Header>
                <Card.Title as="h4">Products</Card.Title>
                <p className="card-category">Add , Delete, Manage Products</p>
              </Card.Header>
              <Card.Body>

              <Table className="table-hover table-striped">
                  <thead>
                    <tr>
                      <th className="border-0">Product Name</th>
                      <th className="border-0">Product Apps</th>
                      <th className="border-0">Visblility</th>
                      <th className="border-0">Un purchased Link</th>
                      <th className="border-0"> purchased Link</th>
                    <td></td>

                      <th className="border-0">
                        <button onClick={showTheAddPop}>Add Product</button>
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                  {productsList}
                  </tbody>
                </Table>
              </Card.Body>
              <Card.Footer>
                <hr></hr>
                <div className="stats">
                  <i className="now-ui-icons loader_refresh spin"></i>
                  Updated 3 minutes ago
                </div>
              </Card.Footer>
            </Card>
          </Col>
        </Row>
      </Container>
      <div className={"thePopUp " + (showEditPop ? "active" : "")} >
          <div className="thePopUpBody">
          {
            editableProduct.apps ? editableProductApps = editableProduct.apps.forEach(app => editApps.push(app.name)) : ''
          }
          <div className="font-icon-detail closePopUp" onClick={closeEditPop}>
            <i className="nc-icon nc-simple-remove"></i>
          </div>
          <h1>Edit Produt</h1>
            <Row>
                <Col>
                <Form.Group>
                    <label>Product Name</label>
                    <Form.Control
                        defaultValue={editableProduct.name}
                        placeholder="Add Product Name"
                        type="text"
                        onChange={hanldeEditableNameChange}
                    ></Form.Control>
                </Form.Group>
                </Col>
            </Row>
            <Row>
            <Col>
                <Form.Group>
                    <label>Product Apps</label>{console.log(editApps.join())}
                    <Form.Control
                        defaultValue={editApps.join()}
                        placeholder="Add Product Apps Separated by Comma"
                        type="text"
                        onChange={hanldeEditableAppsChange}
                    ></Form.Control>
                </Form.Group>
                </Col>
            </Row>
            <Row>
              <Col>
              <label>Product visibility  </label>
              <label className="switch">
              <input type="checkbox" checked={editableProduct.visibility} onChange={toggleShowEditing} />
              <span className="slider round"></span>
            </label>
            </Col>
            </Row>
            {editableProduct.apps ? editableProduct.apps.map((app,i) => {
              return (
                <Row key={i}><Col>
                  <Form.Group>
                    <label>App URL</label>
                    <Form.Control
                      defaultValue={app.url}
                      placeholder="Add Product Page url"
                      type="text"
                      onChange={e => handleEditingAppUrlChange(e, i)}
                    > 
                    </Form.Control>
                  </Form.Group>
                </Col></Row>
              )
            }) : null}
            <Row>
                <Col>
                <Form.Group>
                    <label>Unpurchased URL</label>
                    <Form.Control
                        defaultValue={editableProduct.unpurchasedUrl}
                        placeholder="Add Product url"
                        type="text"
                        onChange={hanldeEditableunpurchasedUrlChange}
                    ></Form.Control>
                </Form.Group>
                </Col>
            </Row>
            <Row>
                <Col>
                <Form.Group>
                    <label>purchased URL</label>
                    <Form.Control
                        defaultValue={editableProduct.purchasedUrl}
                        placeholder="Add Product url"
                        type="text"
                        onChange={hanldeEditablepurchasedUrlChange}
                    ></Form.Control>
                </Form.Group>
                </Col>
            </Row>
            <Row>
                <Col>
                <Button
                    className="btn-fill pull-right"
                    type="submit"
                    variant="info"
                    onClick={()=>{handleEditProduct(editableProduct,setProducts,closeEditPop)}}
                  >
                    Update Product
                  </Button>
                </Col>
            </Row>
            </div>
      </div>

      <div className={"thePopUp " + (showAddPop ? "active" : "")} >
          <div className="thePopUpBody">
            <div className="font-icon-detail closePopUp" onClick={closeAddPop}>
              <i className="nc-icon nc-simple-remove"></i>
            </div>
            <h1>Add Produt</h1>
            <Row>
                <Col>
                <Form.Group>
                    <label>Product Name</label>
                    <Form.Control
                        defaultValue={addingNewProduct.name }
                        placeholder="Add Product Name"
                        type="text"
                        onChange={handleAddingNameChange}
                    ></Form.Control>
                </Form.Group>
                </Col>
            </Row>
            <Row>
              <Col>
              <label>Product visibility  </label>
              <label className="switch">
              <input type="checkbox" checked={addingNewProduct.visibility} onChange={toggleShowAdding} />
              <span className="slider round"></span>
            </label>
            </Col>
            </Row>
            <div>
              {addingNewProduct.apps ? ( addingNewProduct.apps.map((app,i) => (<><Row key={i} style={{display: showApp ? "block" : "none"}}>
              <Col>
              <Form.Group>
                  <label>Product Apps</label>
                  <Form.Control
                    defaultValue={app.name}
                    placeholder="Add Product Apps"
                    type="text"
                    onChange={e => handleAddingAppsChange(e, i)}
                  ></Form.Control>
              </Form.Group>
              </Col>
                <Col>
                <Form.Group>
                  <label>App URL</label>{console.log(addingNewProduct.apps)}
                  <Form.Control
                    defaultValue={app.url}
                    placeholder="Add Product Page url"
                    type="text"
                    onChange={e => handleAddingAppUrlChange(e, i)}
                  > 
                  </Form.Control>
                </Form.Group>
              </Col>
              <Button variant="success"  onClick={e => addComponent(e, app.name, handleAddingAppsChange(e, i), app.url, handleAddingAppUrlChange(e, i))}>+</Button>
              <Button variant="danger">-</Button>
            </Row>
            {createAppComponent}
            </>))
            ) : (<Button variant="success" onClick={() => setShowApp(true)}>Add App</Button>)}
            <Button variant="success"  style={{display: showApp ? "none" : "block"}} onClick={() => setShowApp(true)}>Add App</Button>
            </div>

            <Row>
                <Col>
                <Form.Group>
                    <label>Unpurchased URL</label>
                    <Form.Control
                        defaultValue={addingNewProduct.unpurchasedUrl}
                        placeholder="Add Product Page url"
                        type="text"
                        onChange={handleAddingunpurchasedUrlChange}
                    ></Form.Control>
                </Form.Group>
                </Col>
            </Row>
            <Row>
                <Col>
                <Form.Group>
                    <label>purchased URL</label>
                    <Form.Control
                        defaultValue={addingNewProduct.purchasedUrl}
                        placeholder="Add Product Page url"
                        type="text"
                        onChange={handleAddingpurchasedUrlChange}
                    ></Form.Control>
                </Form.Group>
                </Col>
            </Row>
            <Row>
                <Col>
                <Button
                    className="btn-fill pull-right"
                    type="submit"
                    variant="info"
                    onClick={()=>{handleAddNewProduct(addingNewProduct,setProducts,closeAddPop)}}
                  >
                    Update Product
                  </Button>
                </Col>
            </Row>
            </div>
      </div>
      </>
    )
}

export default addProduct;
