import { db } from "../../firebase";


export const handleDeactiveApp = (user,product,theApp) => {
    
    db.collection("users").doc(user.uid).get().then(snapshot =>{
      let theUser = [];
      
        

    //   theUser.products.forEach( product => {
    //     if(theProduct.name == product.name){
    //         product.apps.forEach(app => {
    //             if(app.name.name == theApp.name.name){
    //                 app.deviceId = " "
    //             }
    //                 console.log(app.deviceId)
    //         })
    //     }
    //   })
    snapshot.data().products.forEach(p=>{
          
        let oneProduct = {};
        oneProduct.name = p.name;
        oneProduct.visibility = p.visibility;
        oneProduct.apps = p.apps;
        oneProduct.comments = p.comment;
        oneProduct.purchased = p.purchased;
        oneProduct.purchasedUrl = p.purchasedUrl;
        oneProduct.unpurchasedUrl = p.unpurchasedUrl;
        oneProduct.datePurchased = p.datePurchased;
        p.apps.forEach(app=>{
            if(oneProduct.name == product.name){
                if(app == theApp.name){
                    oneProduct.apps.push({
                        name:app,
                        active:false,
                        purchased: false,
                        credentials : false,
                        expireDate: "30/3/2021",
                        timesAppLaunchedLastLogin : 3,
                        timesAppLaunchedPurchased : 4,
                        lastLaunchDate : "23-12-2020",
                        timesAppLaunched : 7,
                        deviceId : " "
                    })
                }else {
                oneProduct.apps.push({
                    name:app,
                    active:app.active,
                    purchased: false,
                    credentials : false,
                    expireDate: "30/3/2021",
                    timesAppLaunchedLastLogin : 3,
                    timesAppLaunchedPurchased : 4,
                    lastLaunchDate : "23-12-2020",
                    timesAppLaunched : 7,
                    deviceId : "878866"
                })
                }
            }
        })
        theUser.products.push(oneProduct);
        // console.log("the user is ", theUser.products)
      })
      console.log(theUser)
      return db.collection('users').doc(user.uid).set(theUser) 
      })
    .catch(err=>{
      console.log(err);
    })
  }
