      newAddingCode.products[pIndex].features.map(feature=>{
        if(feature.checked){
          newAddingCode.products[pIndex].checked = true;
        }
      })
      
      setAddingNewCode({...addingNewCode});
    }
    const handleSelectedFeatures = (e, pIndex, featureIndex) => {
      
