                              {/* <Modal.Title>Confirm</Modal.Title> */}
                            </Modal.Header>
                            <Modal.Body style={{marginBottom: 30}}>Are You Sure ?</Modal.Body>
                            <Modal.Footer>
                              <Button variant="secondary" onClick={() => setShowDeleteSelectedCodesAlert(false)}>
                                No
                              </Button>
                              <Button variant="primary" onClick={()=> handleDeleteCodes(selectedCodes, setCodes, closePopUp)}>
                                Yes
                              </Button>
                            </Modal.Footer>
                          </Modal>
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                    {pageNum > 0 ? renderActiveCodes : renderActiveCodesPage}
                    </tbody>
                  </Table>
                  {/* <div className="w-100 px-4 py-5 d-flex flex-row flex-wrap align-items-center justify-content-between">
                    <div className="d-flex flex-row align-items-center">
                      {currentPage && (
                        <span className="current-page d-inline-block h-100 pl-4 text-secondary">
                          Page <span className="font-weight-bold">{currentPage}</span> /{" "}
                          <span className="font-weight-bold">{totalPages}</span>
                        </span>
                      )}
                    </div>
                    <div className="d-flex flex-row py-4 align-items-center">
                      <Pagination
                        totalRecords={totalActiveCodes}
                        pageLimit={18}
                        pageNeighbours={1}
                        onPageChanged={onPageChanged}
                      />
                    </div>
                  </div> */}
