import { db, functions } from "../firebase";


const updateUser  = functions.httpsCallable("updateUser");

export const handleEditAdmin = (admin,setAdmins)=>{
    let theAdmins = []
    // console.log(admin)
    return db.collection("admins").doc(admin.Uid).update({
        email:admin.email,
        firstName:admin.firstName,
        lastName:admin.lastName,
        }).then(()=>{
        db.collection("users").get().then(snapshot=>{
            snapshot.docs.forEach(user=>{
                user.checked = false;
                theAdmins.push(user)
            })
            setAdmins([...snapshot.docs]);
        })
        // updateUsers(admin)
    })
    .catch(err=>{
        console.log("Err is ", err)
    })
}

export const handleDeleteAdmin = (uid,admins,i,setAdmins, callback)=>{
    admins.splice(i, 1);
    setAdmins([...admins]);

    return db.collection("admins").doc(uid).delete()
    .then(() => {
        callback()
    })
    .catch(err=>{
        console.log("Err is ", err)
    })
}

export const handleDeleteAdmins = (ids, admins, setAdmins,callback) =>{
    // console.log("ids", ids)
    ids.forEach (id => {
                // console.log("id", id)
                return db.collection("admins").doc(id).delete();
        })
        setAdmins([...admins]);
        callback()

}

