import React,{useState, useEffect} from 'react'
import { useAuth } from "../contexts/AuthContext";
import { Link, useHistory, Route } from "react-router-dom";
import { 
  Navbar, 
  Container, 
  Row, 
  Col, 
  InputGroup, 
  Button, 
  FormControl,
  Accordion,
  Card,
  Tabs,
  Tab,
  Modal
} from "react-bootstrap";


import { db } from "../../firebase";
import {handleDeactiveApp, handleDeactiveAllApps} from './appAction'


export default function UserPortal() {

  const [theUser, setTheUser] = useState({
    products:[]
  });
  const [key, setKey] = useState('tab1');
  const [showDeactiveAlert, setShowDeactiveAlert] = useState(false);
  const [product, setProduct] = useState({})

  useEffect(() => {
    db.collection("users").doc(currentUser.uid).get()
    .then(snapshot=>{
      // console.log("usersSets", snapshot.id)
      setTheUser({...snapshot.data(),id:currentUser.uid});
      // console.log("user id",theUser.id)

    })
    // console.log(currentUser.uid);

  }, [])

    const { currentUser, logout } = useAuth();
    const history = useHistory();

    const [error, setError] = useState("");

    async function handleLogout() {
        setError("");
    
        try {
          await logout();
          history.push("/login");
        } catch {
          setError("Failed to log out");
        }
      }
      // handleLogout();
    
      const openPopup = product => {
        setProduct(product)
        setShowDeactiveAlert(true)
      }

      const closePopup = () => {
        setShowDeactiveAlert(false)
      }

    let renderUserProducts = null;
    
    renderUserProducts = theUser.products ? theUser.products.map((p,j)=>{
      // const purchasedUrl = new URL(p.purchasedUrl);
      // const unpurchasedUrl = new URL(p.unpurchasedUrl);
      return  <Tabs
      id="uncontrolled-tab-example"
      // activeKey={key}
      // onSelect={(k) => setKey(k)}
      defaultActiveKey="tab0"
      >
        <Tab eventKey={`tab${j}`} title={p.name} key={j} style={{opacity: p.visibility? 1 : 0}}>
              <div className="productUser" style={{borderColor: p.purchased ? "#218838" : "#AAA"}}>
                <Row>
                  <Col xs="auto">
                    <img src={p.productIcon} height="auto" width="50"/>
                  </Col>
                <Col xs="auto">
                  <h4>{p.name}</h4>
                  <p>{p.description}</p>
                  <Button variant="secondary">
                    <a href={p.purchasedUrl ? p.purchasedUrl : p.unpurchasedUrl} target="_blank" >Learn More About {p.name}</a>
                    </Button>
                </Col>
                  <Col xs="auto">
                    <Button style={{height:50, fontSize: 12}} variant="danger" onClick={openPopup}>Deactive all apps</Button>
                    </Col>
                    <Modal 
                    show={showDeactiveAlert} 
                    onHide={closePopup}
                    aria-labelledby="contained-modal-title-vcenter"
                    centered
                    >
                      <Modal.Header closeButton>
                      </Modal.Header>
                      <Modal.Body style={{marginBottom: 30}}>Are You Sure Deactivate All Apps ?</Modal.Body>
                      <Modal.Footer>
                        <Button variant="secondary" onClick={closePopup}>
                          No
                        </Button>
                        <Button variant="primary" onClick={()=>handleDeactiveAllApps(theUser,p,setTheUser,closePopup)}>
                          Yes
                        </Button>
                      </Modal.Footer>
                    </Modal>
                </Row>
                <Row>
                  {p.apps.map((app,i) => {
                    return <Col md="6" key={i}><div className="apps">
                        <div style={{display: "flex", justifyContent: "space-around", marginBottom: 20}}>
                        <img src={app.name.appIcon} height="auto" width="50"/>
                          <h6 style={{marginLeft: 10, marginTop: 10}}>{app.name.name}</h6>
                        <Button className="ml-auto" style={{padding:5, fontSize: 12}} variant="danger" 
                        onClick={(e) => handleDeactiveApp(theUser,p,app,setTheUser)}>Deactive app</Button></div>
                        {/* <p><strong>Expired Date : </strong>{app.expireDate}</p>
                        <p><strong>Last Launched Date : </strong>{app.lastLaunchDate}</p>
                        <p><strong>Times App Launched : </strong>{app.timesAppLaunched}</p>
                        <p><strong>Times App Launched LastLogin: </strong>{app.timesAppLaunchedLastLogin}</p>
                        <p><strong>Times App Launched Purchased : </strong>{app.timesAppLaunchedPurchased}</p> */}
                        <div>{app.active ? <p><i className="icon-ok-sign"></i> Active</p> : <p><i className="icon-remove"></i> Not Active</p>}</div>
                        <div><Button variant="secondary"><a href={app.name.activeUrl ? app.name.activeUrl : app.name.inActiveUrl}>{app.name.activeUrl ? "Purchase" : "Instruction"}</a></Button></div>
                    </div></Col>
                  })}
                </Row>
              </div>
            </Tab>
     </Tabs>
     }) : null  

    return (
      <div>
        <Container>
        <Navbar>
          <Navbar.Brand href="#home"></Navbar.Brand>
          <Navbar.Toggle />
          <Navbar.Collapse className="justify-content-end">
            <Navbar.Text>
            <button onClick={handleLogout}>logout</button>
            </Navbar.Text>
          </Navbar.Collapse>
        </Navbar>

        <div style={{paddingTop : 30, paddingBottom: 30}}>
          <h2 style={{marginBottom: 50}}>User Portal</h2>
          <div style={{display: "flex"}}>
            <strong style={{marginRight:10}}>Welcome </strong>
            <p> {theUser.firstName} {theUser.lastName} - </p>
            <p> {theUser.email}</p>
          </div>
          <div style={{marginTop: 25}}>
          <Button variant="secondary" >Reset Password</Button> 
          <Button variant="secondary" >Store</Button> 
          </div>
            <hr style={{borderTop: "2px solid #555"}} />
          <div className="user_card">
            <strong className="mr-auto mb-3">Activate a Code: </strong>
            {/* <form onSubmit=""> */}
            <InputGroup className="mb-3">
              <FormControl
                placeholder="Your Code"
                aria-label="Your Code"
                aria-describedby="Your Code"
              />
              <InputGroup.Append>
                <Button variant="secondary" type="submit">Activate Code</Button>  
              </InputGroup.Append>
            </InputGroup>
            {/* </form> */}
          </div>
            <hr style={{borderTop: "2px solid #555"}} />
          <Row>
            <Col xs="12">
              {/* <Tabs
              id="uncontrolled-tab-example"
              // activeKey={key}
              // onSelect={(k) => setKey(k)}
              defaultActiveKey="tab0"
              > */}
                {renderUserProducts}
              {/* </Tabs>  */}
            </Col>
          </Row>
        </div>
       
        </Container>
      </div>
    )
}
