import {db} from '../firebase';
// import {crypto} from 'crypto'
const crypto = require("crypto");

export const handleAddCode = (code,setCodes,callback) => {
    const length = Number(code.lengthCode) 
    let cryptoCode = null;
    cryptoCode = crypto.randomBytes(length).toString("hex")
    db.collection("codes").add({
        prefixCode : code.perfixCode,
        code : code.perfixCode+cryptoCode,
        Suffixcode : cryptoCode,
        dateAdded : new Date().toLocaleString(),
        productActivated : code.apps,
        purchasedLocation : code.purchasedLocation,
        notes : code.notes,
    })
    .then(() => {
        db.collection("codes").get().then(snapshot=>{
            let codes = [];
            snapshot.docs.forEach(code=>{
                code.checked = false;
                codes.push(code)
            })
            setCodes([...codes]);
            callback()
        })
    })
    .catch((error) => {
        console.error("Error writing document: ", error);
    });
}

export const handleAddCodes = (code,setCodes,callback) => {
    const length = Number(code.lengthCode) 
    let cryptoCode = null;
    let codes=[]
    for(let i =0; i<code.quantity; i++){
        cryptoCode = crypto.randomBytes(length).toString("hex");
        codes.push(code.perfixCode+cryptoCode)
    }
    db.collection("codes").add({
        perfixCode : code.perfixCode,
        code : codes,
        dateAdded : new Date().toLocaleString(),
        productActivated : code.apps,
        purchasedLocation : code.purchasedLocation,
        notes : code.notes,
    })
    .then((codes) => {
        db.collection("codes").get().then(snapshot=>{
            let codes = [];
            snapshot.docs.forEach(code=>{
                code.checked = false;
                codes.push(code)
            })
            setCodes([...codes]);
            callback()
        })
    })
    .catch((error) => {
        console.error("Error writing document: ", error);
    });
}

export const handleDeleteCode = (uid,codes,i,setCodes)=>{
    codes.splice(i, 1);
    setCodes([...codes]);
    return db.collection("codes").doc(uid).delete()
    .then(() => {
        // callback()
    })
    .catch(err=>{
        console.log("Err is ", err)
    })
}

export const handleDeleteCodes = (ids, setCodes) =>{  
    return db.collection("codes").get().then((snapshot) => {
        snapshot.docs.forEach((code) => {
            let codes = [];
            ids.forEach(uid => {
                db.collection("codes").doc(uid).delete();
                codes.push(code)
            })
            setCodes([...codes])
        })
    }).catch(err=>{
    console.log("ERR",err);
    })
}

