import {db} from '../firebase';
// import {crypto} from 'crypto'
const crypto = require("crypto");

export const handleAddCode = (code,setCodes,callback) => {
    console.log(code.lengthCode)
    const length = Number(code.lengthCode) 
    let cryptoCode = null;
    cryptoCode = crypto.randomBytes(length)
    db.collection("codes").add({
        prefixCode : code.prefixCode,
        code : code.prefixCode+cryptoCode,
        suffixCode : cryptoCode,
        dateAdded : new Date().toLocaleString(),
        productActivated : {apps : code.apps, features: code.features},
        purchasedLocation : code.purchasedLocation,
        notes : code.notes,
    })
    .then(() => {
        db.collection("codes").get().then(snapshot=>{
            let codes = [];
            snapshot.docs.forEach(code=>{
                code.checked = false;
                codes.push(code)
            })
            setCodes([...codes]);
            callback()
        })
    })
    .catch((error) => {
        console.error("Error writing document: ", error);
    });
}

