import React, {useRef,useEffect,useState } from "react";
import ChartistGraph from "react-chartist";
import app, { db } from "../firebase";
import {handleDeleteUser,handleDeleteUsers, handleUpdateUser} from './UsersAction'

import { useAuth } from "../components/contexts/AuthContext"

// react-bootstrap components
import {
  Badge,
  Button,
  Card,
  Navbar,
  Nav,
  Table,
  Container,
  Row,
  Col,
  Form,
  OverlayTrigger,
  Tooltip,
  Modal,
  InputGroup, 
  FormControl ,
  Alert
} from "react-bootstrap";


function Dashboard() {
  const allRef = useRef();
  const emailRef = useRef();
  const firstNameRef = useRef();
  const lastNameRef = useRef();
  const userCode = useRef();
  const passwordRef = useRef();
  const [users,setUsers] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [editableUser,setEditableUser] = useState({
    email:"",
    products:[]
  });
  const [modalAddAccountShow, setModalAddAccountShow] = useState(false);
  const [modalShow, setModalShow] = React.useState(false);
  const [CommentVal, setCommentVal] = React.useState('');
  const [hideSearch, setHideSearch] = useState(true);
  const [searchedVal, setSearchedVal] = useState("");
  const [pageNum, setPageNum] = useState(10);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const { signup } = useAuth();
  const [showDeleteAlert, setShowDeleteAlert] = useState(false)

  const closeEditPop = ()=>{
    setModalShow(false);
    setModalAddAccountShow(false);
}

const handleSubmit = async(e,firstNameRef, lastNameRef,codeRef,setUsers) => {
  e.preventDefault();

  setError("");
  setLoading(true);
  await signup(emailRef.current.value, passwordRef.current.value)
  .then(function(cred){
    db.collection("products").get().then(snapshot=>{
    let theUser = {};
    theUser.email= cred.user.email;
    theUser.password= "";
    theUser.firstName= firstNameRef;
    theUser.lastName= lastNameRef;
    theUser.userCode= codeRef;
    theUser.creationDate=cred.user.metadata.creationTime;
    theUser.lastLoginDate=cred.user.metadata.lastSignInTime;
    theUser.products = [];

    snapshot.docs.forEach(p=>{
      let oneProduct = {};
      oneProduct.name = p.data().name;
      oneProduct.visibility = p.data().visibility;
      oneProduct.apps = [];
      oneProduct.comments = "comment";
      oneProduct.purchased = false;
      oneProduct.purchasedUrl = p.data().purchasedUrl;
      oneProduct.unpurchasedUrl = p.data().unpurchasedUrl;
      oneProduct.productIcon = p.data().productIcon;
      oneProduct.description = p.data().description;
      oneProduct.datePurchased = "22/1/2021";
      p.data().apps.forEach(app=>{
        oneProduct.apps.push({
          name:app,
          activeUrl:app.activeUrl ? app.activeUrl : "",
          inActiveUrl:app.inActiveUrl ? app.inActiveUrl : "",
          appIcon: app.appIcon ? app.appIcon : "",
          tag: app.tag ? app.tag : "",
          active:false,
          purchased: false,
          credentials : false,
          expireDate: "30/3/2021",
          timesAppLaunchedLastLogin : 3,
          timesAppLaunchedPurchased : 4,
          lastLaunchDate : "23-12-2020",
          timesAppLaunched : 7,
          deviceId : "1.23.456.5",
          activatedOn: '12/28/2020 @ 5:45pm : "1.23.456.5"',
          codeUsed: '123456789' ,
          DateofLastUse: '12/29/2020 @ 3:43pm',
          LaunchesSinceActive: 23
        })
        // console.log("the user is ", app.url)
      })
      theUser.products.push(oneProduct);
    })
    // setUsers({...allUsers});
    return db.collection('users').doc(cred.user.uid).set(theUser) 
    })
  }) 
  .catch(err=>{
    setError("Failed to create an account");
  })

  setLoading(false);
}

const resetPassword = () => {

}

  useEffect(() => {
    db.collection("users").get()
    .then(snapshot=>{
      let users = [];
      snapshot.docs.forEach(user=>{
        user.checked = false;
        user.purchased = false;
        users.push(user);
      })
