import React, {useState, useRef, useEffect} from "react";
import {db} from '../firebase';

// react-bootstrap components
import {
    Button,
    Card,
    Table,
    Container,
    Row,
    Col,
    Form,
    OverlayTrigger,
    Tooltip,
    Modal,
    Tabs,
    Tab
} from "react-bootstrap";

function AddCodes() {
    const [showCodePopup, setShowCodePopup] = useState(false);
    const [products, setProducts] = useState({});
    const [addingNewCode, setAddingNewCode] = useState({});

    useEffect(() => {
        db.collection("products").get().then(snapshot => {
            let allProducts = [];
            snapshot.docs.forEach(p => {
                allProducts.push(p)
            })
            setProducts([...allProducts])
        }).catch(err => {
            console.log(err)
        })
    }, [])

    const showTheAddPop = ()=>{
        setShowCodePopup(true);
        let addingNewCode = {};
        addingNewCode.name  = "";
        addingNewCode.description  = "";
        addingNewCode.visibility  = false;
        addingNewCode.unpurchasedUrl  = "";
        addingNewCode.purchasedUrl  = "";
        addingNewCode.productIcon  = null;
        addingNewCode.apps  = [{name:"", activeUrl:"", inActiveUrl: "", tag: "", appIcon: null}];
        setAddingNewCode({...addingNewCode});
    }
    const closeAddPop = () => {
        setShowCodePopup(false)
    }
  return (
    <>
      <Container fluid>
        <Row>
          <Col md="12">
            <Card className="strpied-tabled-with-hover table-bordered">
              <Card.Header>
                <Card.Title as="h4">Codes</Card.Title>
              </Card.Header>
              <Card.Body className="table-full-width table-responsive px-0">
                <Table className="table-hover table-striped">
                  <thead>
                    <tr>
                      <th className="border-0">
                         <Form.Check  className="mb-1 pl-0" style={{display:"inline-block"}}>
                            <Form.Check.Label>
                              <Form.Check.Input
                                // onChange={toggleProductsSelection}
                                // ref={allRef}
                                defaultValue="x"
                                type="checkbox"
                              ></Form.Check.Input>
                              <span className="form-check-sign"></span>
                            </Form.Check.Label>
                          </Form.Check>
                          <span>all</span>
                        </th>
                      <th className="border-0">Code</th>
                      <th className="border-0">Product Activated</th>
                      <th className="border-0">Date Added</th>
                      <th className="border-0">Place Of Purchase</th>
                        
                      {/* <th className="border-0">Activated By</th>
                      <th className="border-0">Date Activated</th>
                      <th className="border-0">Place Of Purchase</th>
                      <th className="border-0">View Code Info</th> */}

                      <th className="border-0">
                        <button onClick={showTheAddPop}>Add Code</button>
                        <button onClick={() => console.log("Deleted Selected")}>Delete Selected Codes</button>
                        <button onClick={() => console.log("Deleted Selected")}>Export Selected Code to CSV</button>
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                    <td>
                    <Form.Check className="mb-1 pl-0">
                    <Form.Check.Label>
                        <Form.Check.Input
                        onChange={(e)=> console.log("check Box")}
                        // checked={product.checked}
                            defaultValue=""
                            type="checkbox"
                        ></Form.Check.Input>
                        <span className="form-check-sign"></span>
                        </Form.Check.Label>
                    </Form.Check>
                    </td>
                        <td>1</td>
                        <td>Dakota Rice</td>
                        <td>$36,738</td>
                        <td>Niger</td>
                        <td>
                        <OverlayTrigger overlay={<Tooltip id="tooltip-422471719">Edit Task..</Tooltip>}>
                        <Button
                            className="btn-simple btn-link p-1"
                            type="button"
                            variant="info"
                            onClick = {()=> console.log("Edit code")}
                        ><i className="fas fa-edit"></i>
                        </Button>
                        </OverlayTrigger>
                        <OverlayTrigger
                        overlay={<Tooltip id="tooltip-829164576">Remove..</Tooltip>}>
                        <Button
                            className="btn-simple btn-link p-1"
                            type="button"
                            variant="danger"
                            onClick = {() =>console.log("remove Code")}
                        ><i className="fas fa-times"></i>
                        </Button>
                        </OverlayTrigger> 
                    </td>

                        {/* <td>
                        <OverlayTrigger overlay={<Tooltip id="tooltip-422471719">View Task..</Tooltip>}>
                        <Button
                            className="btn-simple btn-link p-1"
                            type="button"
                            variant="info"
                            onClick = {()=> console.log("View code")}
                        >View</Button>
                        </OverlayTrigger>
                       </td> */}

                    </tr>
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
            <div className={"thePopUp " + (showCodePopup ? "active" : "")} >
              <div className="thePopUpBody" style={{maxWidth: "770px "}}>
                    <div className="font-icon-detail closePopUp" onClick={closeAddPop}>
                    <i className="nc-icon nc-simple-remove"></i>
                    </div>
                    <h2 style={{marginBottom:50}}>Add Code</h2>
                <Tabs defaultActiveKey="SingleCode" id="uncontrolled-tab-example">
                    <Tab eventKey="SingleCode" title="Single Code" style={{paddingTop: 40}}>
                    <Form.Group>
                    <Row>
                        <Col md="4"><Row><Col xs="auto"><label>Code Perfix: </label></Col>
                    <Col>
                    <Form.Control
                            // defaultValue={addingNewProduct.name }
                            type="text"
                            // onChange={handleAddingNameChange}
                        ></Form.Control></Col></Row></Col>
                        <Col md="4">
                        <Button
                            className="btn-fill pull-right"
                            type="submit"
                            variant="secondary"
                            onClick={()=> console.log("Add Code")}
                        >
                            Generate Random Code
                        </Button>
                        </Col>
                        <Col md="4">
                        <Row><Col xs="auto"><label>Code Length: </label></Col>
                    <Col>
                    <Form.Control
                            // defaultValue={addingNewProduct.name }
                            type="number"
                            // onChange={handleAddingNameChange}
                        ></Form.Control></Col></Row>
                        </Col>
                    </Row>
                    </Form.Group>
                    <Form.Group>
                <Row>
                    <Col xs="auto"><label>Purchased Location: </label></Col>
                    <Col xs="auto"><Form.Control
                            // defaultValue={addingNewProduct.unpurchasedUrl}
                            placeholder="Add Product Page url"
                            type="text"
                            // onChange={handleAddingunpurchasedUrlChange}
                        ></Form.Control></Col>
                    </Row>
                    </Form.Group>
                    <hr />
                    <Row>
                        {console.log(products)
                            // products.map((p,i) => {
                            //     return (
                            //         <h6>{p.data().name}</h6>

                            //     )
                            // })
                        }
                    </Row>
                <Row>
                <Col>
                <Button
                    className="btn-fill pull-right"
                    type="submit"
                    variant="info"
                    onClick={()=> console.log("Add Code")}
                  >
                    Add Code
                  </Button>
                </Col>
            </Row>
                </Tab>
                <Tab eventKey="BulkCode" title="Bulk Code" style={{paddingTop: 40}}>
                    <Form.Group>
                    <Row><Col xs="auto"><label>Product Name</label></Col>
                    <Col>
                    <Form.Control
                            // defaultValue={addingNewProduct.name }
                            placeholder="Add Product Name"
                            type="text"
                            // onChange={handleAddingNameChange}
                        ></Form.Control></Col>
                    </Row>
                    </Form.Group>
                <Row>
                    <Col>
                    <Form.Group>
                    <Row><Col xs="auto"><label style={{marginBottom: 15}}>Product Description</label></Col>
                        <Col><Form.Control
                            // defaultValue={addingNewProduct.description }
                            placeholder="Add Product Name"
                            type="text"
                            // onChange={handleAddingDescriptionChange}
                        ></Form.Control></Col></Row>
                    </Form.Group>
                    </Col>
                </Row>
                <Row>
                    <Col>
                    <Form.Group>
                        <label>Unpurchased URL</label>
                        <Form.Control
                            // defaultValue={addingNewProduct.unpurchasedUrl}
                            placeholder="Add Product Page url"
                            type="text"
                            // onChange={handleAddingunpurchasedUrlChange}
                        ></Form.Control>
                    </Form.Group>
                    </Col>
                </Row>
                <Row>
                <Col>
                <Button
                    className="btn-fill pull-right"
                    type="submit"
                    variant="info"
                    onClick={()=> console.log("Add Code")}
                  >
                    Add Code
                  </Button>
                </Col>
            </Row>
                </Tab>
                </Tabs>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </>
  );
}

export default AddCodes;
