import {db} from '../firebase';
// import {crypto} from 'crypto'
const crypto = require("crypto");

export const hadleAddCode = (code) => {
    const length = code.lengthCode;
    let cryptoCode = null;
    cryptoCode = code.perfixCode+crypto.randomBytes(length).toString("hex")
    // db.collection("codes").add({
